       		if( $(this).attr('aria-expanded') == 'false' ){
            	$(this).attr('aria-expanded', 'true');
                $(toControl)
                	.attr('aria-hidden', 'false')
                    .slideDown(400);
                $('a', toControl).attr('tabindex', 0);
                $('a', toControl).first().focus();
            }else{
            	$(this).attr('aria-expanded', 'false');
                $(toControl)
                	.attr('aria-hidden', 'true')
                	.slideUp(400);
                $('a', toControl).attr('tabindex', -1);
            }
       	});

        //HIDE TAGLINE
