function LoadGroupedData(container, MIID, PMI, groupYear, groupMonth, groupBy, tag) {
  //ViewToUse looks at the hidden Sidebar List View defined in the Builder View.
  var viewToUse = "";
    if($('#hid-'+MIID+'-SidebarListView').length > 0){
      viewToUse = $("#hid-" + MIID + "-SidebarListView").val();
    };
  GetContent('https://www.bismarckschools.org//cms/UserControls/ModuleView/ModuleViewRendererWrapper.aspx?DomainID=832&PageID=3802&ModuleInstanceID=' + MIID + '&PageModuleInstanceID=' + PMI + '&RenderLoc=0&FromRenderLoc=0&GroupYear=' + groupYear + '&GroupMonth=' + groupMonth + '&GroupByField=' + groupBy + '&EnableQuirksMode=0&ViewID=' + viewToUse + '&Tag=' + tag, container, 2, 'chkSidebar();');
}
function LoadData(container, MIID, PMI, flexDataID, groupYear, groupMonth, groupBy, targetView, tag) {
  if(targetView !== undefined || targetView.Length() == 0){
  //targetView looks at the hidden Detail View defined in the Builder View.
      targetView = $('#hid-'+MIID+'-DetailView').val();
   }
  GetContent('https://www.bismarckschools.org//cms/UserControls/ModuleView/ModuleViewRendererWrapper.aspx?DomainID=832&PageID=3802&ModuleInstanceID=' + MIID + '&PageModuleInstanceID=' + PMI + '&FlexDataID=' + flexDataID + '&GroupYear=' + groupYear + '&GroupMonth=' + groupMonth + '&GroupByField=' + groupBy + '&RenderLoc=0&FromRenderLoc=0&EnableQuirksMode=0&Tag=' + tag + '&ViewID=' + targetView, container, 2, 'chkSidebar();');
}
function LoadTaggedData(container, MIID, PMI, tag) {
  //ViewToUse looks at the hidden Sidebar List View defined in the Builder View.
  var viewToUse = "";
    if($('#hid-'+MIID+'-SidebarListView').length > 0){
      viewToUse = $("#hid-" + MIID + "-SidebarListView").val();
    };
  GetContent('https://www.bismarckschools.org//cms/UserControls/ModuleView/ModuleViewRendererWrapper.aspx?DomainID=832&PageID=3802&ModuleInstanceID=' + MIID + '&PageModuleInstanceID=' + PMI + '&RenderLoc=0&FromRenderLoc=0&Tag=' + tag + '&EnableQuirksMode=0&ViewID='+viewToUse, container, 2, 'chkSidebar();');
  setTimeout(function(){ $("#module-content-"+ MIID +"").find('[tabindex]:first').focus(); }, 200);
}

        $(document).ready(function() {
            var DomainID = '832';
            var PageID = '3802';
            var RenderLoc = '0';
            var MIID = '7020';

            //added to check to make sure moderated content doesn't bleed through the dialog
            if ($('#dialog-overlay-WindowLargeModal-body.moderateContent').length > 0) {
                $("#module-content-" + MIID).find(".ui-widget-detail").find(".ui-article").append("
 ");
            }
        });

	// APP SETTINGS

    // SETTINGS OBJECT WITH USER SAVED SETTINGS
    var csAppSettings7020 = {"csInitViewOptions": "3", "csViewOptions": "1", "csActiveHeaderBgColor": "", "csActiveHeaderTxtColor": "", "csInactiveHeaderBgColor": "", "csInactiveHeaderTxtColor": "", "csShowBullets": "False", "csShowAppHeader": "False"};


    // TURN HEADER ON IF NEEDED
    if(csAppSettings7020.csShowAppHeader == "True") {
        $("#module-content-7020 .ui-widget-header").removeClass("hidden");
    }

	$(document).ready(function() {
        csInit7020();
	});

    function csInit7020() {
    	switch(csAppSettings7020.csInitViewOptions) {
        	case "1":
            	//nothing to be done here
            break;
            case "2":
            	$("#module-content-7020 .ui-widget.app.content-accordion .ui-article").addClass("active");
                $("#module-content-7020 .ui-widget.app.content-accordion .ui-article .ui-article-title").attr("aria-expanded","true");
                $("#module-content-7020 .ui-widget.app.content-accordion .ui-article .ui-article-detail").css("display","block").attr("aria-hidden","false");
            break;
            case "3":
            	$("#module-content-7020 .ui-widget.app.content-accordion li:eq(0) .ui-article").addClass("active");
                $("#module-content-7020 .ui-widget.app.content-accordion li:eq(0) .ui-article .ui-article-title").attr("aria-expanded","true");
            	$("#module-content-7020 .ui-widget.app.content-accordion li:eq(0) .ui-article .ui-article-detail").css("display","block").attr("aria-hidden","false");
            break;
        }

        switch(csAppSettings7020.csViewOptions) {
        	case "1":
                $("#module-content-7020 .ui-widget.app.content-accordion .ui-article-title").click(function(){
                    if($(this).parent().hasClass("active")){
                    	$(this).attr("aria-expanded","false");
                        $(this).parent().find(".ui-article-detail").slideUp().attr("aria-hidden","true");
                        $(this).parent().removeClass("active");
                    } else {
                    	$(this).attr("aria-expanded","true");
                        $(this).parent().find(".ui-article-detail").slideDown().attr("aria-hidden","false");
                        $(this).parent().addClass("active");
                    }
                });

                $("#module-content-7020 .ui-widget.app.content-accordion .ui-article-title").keydown(function(e) {

                	var csKeyCodes = { "enter": 13, "space": 32 };

                    // CAPTURE KEY CODE
                    switch(e.keyCode) {
                        // CONSUME DOWN ARROW
                        case csKeyCodes.enter:
                        case csKeyCodes.space:
                            e.preventDefault();

                            if($(this).parent().hasClass("active")){
                            	$(this).attr("aria-expanded","false");
                                $(this).parent().find(".ui-article-detail").slideUp().attr("aria-hidden","true");
                                $(this).parent().removeClass("active");
                            } else {
                            	$(this).attr("aria-expanded","true");
                                $(this).parent().find(".ui-article-detail").slideDown().attr("aria-hidden","false");
                                $(this).parent().addClass("active");
                            }

                        break;
                    }
                });

            break;
            case "2":
				$("#module-content-7020 .ui-widget.app.content-accordion .ui-article-title").click(function(){
                    if($(this).parent().hasClass("active")){
                    	$(this).attr("aria-expanded","false");
                        $(this).parent().find(".ui-article-detail").slideUp().attr("aria-hidden","true");
                        $(this).parent().removeClass("active");
                    } else {
                        $("#module-content-7020 .ui-widget.app.content-accordion .active .ui-article-detail").slideUp().attr("aria-hidden","true");
                        $("#module-content-7020 .ui-widget.app.content-accordion .active .ui-article-title").attr("aria-expanded","false");
                        $("#module-content-7020 .ui-widget.app.content-accordion .active").removeClass("active");
                        $(this).attr("aria-expanded","true");
                        $(this).parent().find(".ui-article-detail").slideDown().attr("aria-hidden","false");
                        $(this).parent().addClass("active");
                    }
                });

                $("#module-content-7020 .ui-widget.app.content-accordion .ui-article-title").keydown(function(e) {

                	var csKeyCodes = { "enter": 13, "space": 32 };

                    // CAPTURE KEY CODE
                    switch(e.keyCode) {
                        // CONSUME DOWN ARROW
                        case csKeyCodes.enter:
                        case csKeyCodes.space:
                            e.preventDefault();

                            if($(this).parent().hasClass("active")){
                            	$(this).attr("aria-expanded","false");
                                $(this).parent().find(".ui-article-detail").slideUp().attr("aria-hidden","true");
                                $(this).parent().removeClass("active");
                            } else {
                                $("#module-content-7020 .ui-widget.app.content-accordion .active .ui-article-detail").slideUp().attr("aria-hidden","true");
                                $("#module-content-7020 .ui-widget.app.content-accordion .active .ui-article-title").attr("aria-expanded","false");
                                $("#module-content-7020 .ui-widget.app.content-accordion .active").removeClass("active");
                                $(this).attr("aria-expanded","true");
                                $(this).parent().find(".ui-article-detail").slideDown().attr("aria-hidden","false");
                                $(this).parent().addClass("active");
                            }

                        break;
                    }
                });

            break;
        }

        // ADD THE DYNAMIC STYLESHEET
        var dynStyleSheet = document.createElement('style');
        if(dynStyleSheet) {
            dynStyleSheet.setAttribute('type', 'text/css');
            var head = document.getElementsByTagName('head')[0];

            if(head) {
                head.insertBefore(dynStyleSheet, head.childNodes[0]);
            }

            // CHECK IF BULLETS ARE NEEDED
            var csBullets = "";
            if(csAppSettings7020.csShowBullets == "True") {
                csBullets = '#module-content-7020 .ui-widget.app.content-accordion .ui-article.active .ui-article-title:after {' +
                                'content: "_";' +
                                'position: absolute;' +
                                'top: 4px;' +
                                'right: 18px;' +
                                'width: 0px;' +
                                'color: ' + csAppSettings7020.csActiveHeaderTxtColor + ';' +
                            '}' +
                            '#module-content-7020 .ui-widget.app.content-accordion .ui-article .ui-article-title:after {' +
                                'content: "+";' +
                                'position: absolute;' +
                                'top: 9px;' +
                                'right: 18px;' +
                                'width: 0px;' +
                                'color: ' + csAppSettings7020.csInactiveHeaderTxtColor + ';' +
                            '}';
            }

            var dynStyles = '#module-content-7020 .ui-widget.app.content-accordion .ui-widget-header {' +
                                'margin-bottom: 6px;' +
                            '}' +
                            '#module-content-7020 .ui-widget.app.content-accordion .ui-article {' +
                                'padding: 0px 0px 6px 0px;' +
                            '}' +
                            '#module-content-7020 .ui-widget.app.content-accordion .ui-article.active .ui-article-title:before {' +
                                'content: "";' +
                                'position: absolute;' +
                                'bottom: -7px;' +
                                'left: 13px;' +
                                'width: 0px;' +
                                'height: 0px;' +
                                'border-style: solid;' +
                                'border-width: 7px 7px 0 7px;' +
                                'border-color: ' + csAppSettings7020.csActiveHeaderBgColor + ' transparent transparent transparent;' +
                            '}' +
                            '#module-content-7020 .ui-widget.app.content-accordion .ui-article.active .ui-article-title {' +
                                'background-color: ' + csAppSettings7020.csActiveHeaderBgColor + ';' +
                                'color: ' + csAppSettings7020.csActiveHeaderTxtColor + ';' +
                                'border-radius: 1px;' +
                                '-moz-border-radius: 1px;' +
                                '-webkit-border-radius: 1px;' +
                                'position: relative;' +
                            '}' +
                            '#module-content-7020 .ui-widget.app.content-accordion .ui-article .ui-article-title {' +
                                'background-color: ' + csAppSettings7020.csInactiveHeaderBgColor + ';' +
                                'color: ' + csAppSettings7020.csInactiveHeaderTxtColor + ';' +
                                'padding: 10px;' +
                                'position: relative;' +
                                'cursor: pointer;' +
                            '}' +
                            csBullets;

            var rules = document.createTextNode(dynStyles);

            if(dynStyleSheet.styleSheet){ // IE
                dynStyleSheet.styleSheet.cssText = dynStyles;
            } else {
                dynStyleSheet.appendChild(rules);
            }
        }
    }

