	var csHeaderIsSticky = 0, csNavIsSticky = 0;
	var isMyViewPage = ($("#pw-body").length) ? 1 : 0;
	var districtAlias = "District";
	var defaultLogoSrc = "/cms/lib/ND01911460/Centricity/template/globalassets/images/logos/fargo-logo.png";

	$(window).load(function(){
		searchText();
		calendarListView();
		stickyHeader();
		//setupBullets(); //must be called here so that the actually bullets will exist. They get added by a system function sometime on-ready
	});

	$(function(){
		if( $('#hp-emergency-annc .ui-articles li').length > 0 ){
			// Removed By Chris Case 00202592 $('#hp-emergency-annc .ui-widget-header').prepend('!');
			$('#hp-emergency-annc').removeClass('hidden');
		}

		$(window).bind("resize", function(){ pgScreenSize(); calendarListView(); stickyHeader(); });
		$(window).scroll(function() { stickyHeader(); });

		$.ajax({
			url: '/cms/lib/ND01911460/Centricity/Template/17/scripts/no-support-fargo-school-dropdown.html',
			complete: function(data){
				$('#gb-schools').append(data.responseText);
				$('#school-dropdown-custom-links').append($('#school-list-custom-links > a'));

				$('.school-dropdown-trigger').click(function(e){
					var modifier = '';
					if( $(this).hasClass('open-menu') ){ //close menu
						$(this)
                        	.attr('aria-expanded', 'false')
                        	.removeClass('open-menu');
                        $('#gb-schools').attr('aria-hidden', 'true');
						modifier = "-";
					}else{ //open menu
						$(this)
                        	.attr('aria-expanded', 'true')
                        	.addClass('open-menu');
						$('#gb-schools').attr('aria-hidden', 'false');
					}

					animateSchoolDropdown(modifier);
				});

				var extraSchoolOption = '';
				var extraSchoolItems = '';

				$('#school-dropdown-groups .school-group').each(function(k, v){
					extraSchoolOption += $(".group-header h1", v).text().trim() + ";";

					$('.school-lists li', v).each(function(k1, v1){
						var sLink = $('a', v1);
    						extraSchoolItems += (k + 1) + ',' + sLink.text() + ',' + sLink.attr('href') + ',_self;';
					});
				});
				extraSchoolOption = extraSchoolOption.substring(0, extraSchoolOption.length - 1); //strip the extra semi-colon

                //remove unused school dropdown links.
                $('#school-dropdown-custom-links .school-dropdown-button:empty').remove();

                //mark the last school-item
                $('#school-dropdown-groups > div:last .school-lists > ul:last > li:last a').addClass('last-a');


                //adjust the tabindex on all 's
                $('#gb-schools a').attr('tabindex', -1);

				// RESPONSIVE MAIN MENU
				$("body").csRsMenu({
				        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
					"slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
					"primaryColor"          : "#6D7684", // DEFAULT - #2C363E
					"secondaryColor"        : "#3A3F46", // DEFAULT - #222A30
					"accentColor"           : "#6D7684", // DEFAULT - #B5BBBF
					"menuButtonParent"      : "#gb-channel-list",
					"showMenuBtnText"       : "yes",
					"menuBtnText"           : " MENU",
					"showSchools"           : "no",
					"showTranslate"         : "yes", /* Changed from no by Jeremy - Case #00180889 */
					"translateId"           : "none",
					"showAccount"           : "no",
					"extraMenuOptions"      : extraSchoolOption, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
					"extraMenuItems"        : extraSchoolItems, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
					"siteID"                : "4",
					"allLoaded" : function(){ 

						//add in the school dropdown extra links
						var extraLinks = '';

						$('#school-dropdown-custom-links .school-dropdown-button').each(function(k, v){
							if( $(v).text().trim() != "" ){
								extraLinks += '

';
								extraLinks += '' + $(v).text().trim() + '';
							}
						});

						$('#rs-menu .rs-menu-channels .rs-menu-option-list').append(extraLinks);

						//add in the staff directory link

							//add by Jeremy - Case #00180889
						$('#rs-menu .rs-menu-channels .rs-menu-option-list').append('\

\

\
									\
										Staff Directory\
									\
								\
							\
						');
					}
				});

				sameHeights($('#school-dropdown-cont .school-group'), true, ['.group-header', '.school-lists']);
				$(window).resize(function(e){	//we have to hook it here to make sure it exists and no JS errors will be called.
					sameHeights($('#school-dropdown-cont .school-group'), true, ['.group-header', '.school-lists']);
				});

				$('#school-dropdown-groups > .school-group').click(function(e){
					if( $(this).hasClass('open-menu') ){
						$(this).removeClass('open-menu');
					}else{
						$('#school-dropdown-groups > .school-group.open-menu').removeClass('open-menu');
						$(this).addClass('open-menu');
					}
				});
			}
		});

		function animateSchoolDropdown(modifier){
			var currH = $('#sticky-header-ghost').outerHeight();

			$('#gb-schools').animate({
				'height': 'toggle'
			},
			{
				'step': function(e, a){					
					stickyHeader(); //should update the height of the sticky header as it steps
				}
			});

			$('.school-dropdown-trigger .rotating-icon').toggleClass('point-down').toggleClass('point-up');

            //find focus element
            if(modifier == "-"){
            	$('#gb-schools a').attr('tabindex', -1);
            }else{
            	$('#gb-schools a').attr('tabindex', 0);
                if( $('#school-dropdown-custom-links').children().length > 0 ){
                    $('#school-dropdown-custom-links > a:first').addClass('first-a').focus();
                }else{
                    $('#school-dropdown-groups > div:first .school-lists > ul:first > li:first a').addClass('first-a').focus();
                }

                $('#gb-schools a.first-a').off('keydown').on('keydown', function(e) {
                    if ((e.shiftKey && e.keyCode == 9) ) {
                        e.preventDefault();
                        $('#gb-custom-mystart .school-dropdown-trigger').focus();
                    }
                });

                $('#gb-schools a.last-a').off('keydown').on('keydown', function(e) {
                	var code = e.which;
                    if ((code === 9) ) {
                        e.preventDefault();
                        $('#gb-custom-mystart .school-dropdown-trigger').focus();
                    }
                });
			}
        }

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
		        "breakPoint"            : 480, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
			"primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			"secondaryColor"        : "#222A30", // DEFAULT - #222A30
			"accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			"menuButtonParent"      : "#sp-content",
			"showMenuBtnText"       : "yes",
			"menuBtnText"           : "PAGES",
			"siteID"                : "4"
		});

		pgScreenSize();
		useChannelForSection();
