			if( $(window).width() >= 1024 ){
				/* Removed by Jeremy K - Case #02940545 11/2/17

                if( $('.school-dropdown-trigger').hasClass('open-menu') ){
					$('.school-dropdown-trigger').removeClass('open-menu');
					animateSchoolDropdown("-");
				}
                */
			}
		});

		if( districtAlias != 'District' ){
			$('#gb-page').addClass('subsite');
		}else{
			$('#gb-page').addClass('district');
		}

		addFramesetTranslate();

		$('main .row.three .headlines .ui-read-more').remove();
	});	


	function pgScreenSize(runningFrom){
		var getWidth = $(window).width();
		var initW = 1238, initH = 495; /* used to calculate headline card container sizing */
		var mobileBp = false;

		if (getWidth >= 1024) { //Desktop View
			matchHomepageColumnHeights();

			sameHeights($('#gb-footer > .column'));	

			if( $('#hp-content > .row.two.moving').length > 0 ){
				$('.row.two.moving').appendTo($('#hp-content .column.one'));
			}

			if( $('#sticky-header > #gb-channel-list-outer').length > 0 ){
				$('#hp-slideshow-outer').after($('#gb-channel-list-outer'));
			}

			$('#gb-logo').css('width', 'calc( 100% - ' + $('#gb-global-icons').outerWidth() + 'px)');

		}else{ //Mobile BPs
			if( $('#hp-content > .row.two.moving').length <= 0 ){
				$('.row.two.moving').appendTo($('#hp-content'));
			}

			if( $('#sticky-header > #gb-channel-list-outer').length <= 0 ){
				$('#sticky-header').append($('#gb-channel-list-outer'));
			}

			if(getWidth < 640){ //run on 480 bp
				appView(['.hp main']);
				initW = 640;
				initH = 1066;
				if( getWidth < 480 ){ //run on 320 bp
					initW = 320;
					initH = 2240;
				}
				mobileBp = true;
			}else if( getWidth < 768 ){ //run on 640 bp
				appView(['#hp-content .row.two.moving']);
				initW = 640;
				initH = 1066;
			}

		}

		multimediaRotator();

		var cont = $('main .row.three .headlines .ui-widget-detail');

		cont.show();

		var contW = cont.width();
                var ratio = initH / initW;
                var contHeight = contW * ratio;
                cont.css('height', contHeight);

                if( mobileBp ) cont.hide();
	}

	function multimediaRotator(){
		if(!$("#sw-content-container13.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container13.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : 850,
		                "imageHeight"             : 405,
		                "efficientLoad"           : "yes",
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "no",
		                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "yes",
		                "contMoreLink"            : "yes",
		                "contMoreLinkText"        : "MORE",
		                "descMoreLink"            : "no",
		                "descMoreLinkText"        : "Read More",
		                "bullets"                 : "yes",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
		                "fullScreenBreakpoints"   : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
		                "onImageLoad"             : function() { },
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() { 
		                	if( !$('#hp-slideshow .rs-video-button').hasClass('hidden') ){
		                		$('#hp-slideshow .rs-video-button').addClass('hidden');
		                	}
		                },
		                "onTransitionEnd"         : function() {
		                	centerCaption();
		                	if( $('#hp-slideshow .rs-gallery-images-list > li.active .watch-btn').length > 0 ){
		                		$('#hp-slideshow .rs-video-button').removeClass('hidden');
		                	}
		                },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() {

		                	$('#hp-slideshow .rs-gallery-desc').prepend('
');
		                	$('#hp-slideshow .rs-gallery-desc').append('


');

		                	if( $('#hp-slideshow .rs-gallery-control').length > 0 ){
						$('#hp-slideshow .rs-gallery-container > .rs-gallery-control.next, #hp-slideshow  .rs-gallery-container > .rs-gallery-control.back').clone(true).appendTo('#hp-slideshow .rs-gallery-control-cont.top-controls')
		                		$('#hp-slideshow .rs-gallery-container > .rs-gallery-control').appendTo('#hp-slideshow .rs-gallery-control-cont:not(.top-controls)');
		                	}

		                	if( $('#hp-slideshow .rs-gallery-bullets-outer').length > 0 ){
		                		$('#hp-slideshow .rs-gallery-bullets-outer')
		                			.appendTo('#hp-slideshow .rs-gallery-control-cont:not(.top-controls)')
		                			.clone(true)
		                			.appendTo('#hp-slideshow .rs-gallery-control-cont.top-controls');

		                				//this fixes an issue with the rotator not updating multiple bullet containers on the page.
		                			$('#hp-slideshow .rs-gallery-bullet').click(function(e){
		                				$('#hp-slideshow .rs-gallery-bullets-outer .rs-gallery-bullet.active').removeClass('active');
		                				var clickIndex = $(this).index();
		                				$('#hp-slideshow .rs-gallery-bullets-outer').each(function(k, v){
		                					$('.rs-gallery-bullet:eq(' + clickIndex + ')', v).addClass('active');
		                				});
		                			});
		                	}

		                	if( $('#hp-slideshow .rs-gallery-more-link').length > 0 ){
		                		$('#hp-slideshow .rs-gallery-control-cont:not(.top-controls) .rs-gallery-bullets-outer').before($('#hp-slideshow .rs-gallery-more-link'));
		                	}

		                	$('#hp-slideshow .rs-gallery-control-cont:not(.top-controls)').append('
');

		                	if( $('#hp-slideshow .rs-gallery-title').length > 0 ){
		                		$('#hp-slideshow .rs-gallery-title').appendTo('#hp-slideshow .rs-gallery-desc-inner');
		                	}

		                	if( $('#hp-slideshow .rs-gallery-caption').length > 0 ){
		                		$('#hp-slideshow .rs-gallery-caption').appendTo('#hp-slideshow .rs-gallery-desc-inner');
		                	}

		                	if( $('#hp-slideshow .rs-gallery-control').length > 0 ){
						$('#hp-slideshow .rs-gallery-control').addClass('icon-font');
					}

		                	$("#hp-slideshow .rs-video-button").click(function(){
						$("#hp-slideshow ul.rs-gallery-images-list li.active .watch-btn").trigger( "click" );
					});

					if( $('#hp-slideshow .rs-gallery-images-list > li.active .watch-btn').length < 1 ){
		                		$('#hp-slideshow .rs-video-button').addClass('hidden');
		                	}

					centerCaption();

					$('#hp-slideshow .rs-gallery-control-cont:not(.top-controls) > div:not(.hidden):eq(0)').addClass('first');
		                }
			});
		}
	}

	function centerCaption(){
		if( $(window).width() >= 1024){
			var middlePos = (($('#hp-slideshow .rs-gallery-desc-cont').height()/2) - (parseInt($('#hp-slideshow .rs-gallery-desc-inner').height())/2 ) + 15); /* 30 is the offset to bump it down off center */
			$('#hp-slideshow .rs-gallery-desc-inner').animate({'top': middlePos}).css('bottom', 'auto');
		}
	}

	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "yes",
	            dayWeek     : "yes"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
			    var moveArticle = $(this).html();
			    $(this).parent().prev().children().children().next().append(moveArticle);
			    $(this).parent().remove();
			};
		});

	    	$(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '
';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}
			});
		});
	}

	function appView(toChange){
	    //DOC make homepage apps collapsible for small mobile devices

	    $.each(toChange, function(k, v){
	        //DOC dont add app into accordion if it doesnt have a header
	        $(v + " div.ui-widget.app").each(function() {
	            if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
	                $(this).addClass("no-header");
	            }else{
	            	if( $('.ui-widget-header .accordion-dropdown-icon', this).length <= 0 ){
	            		$('.ui-widget-header').append('');
	            	}
	            }
	        });

	        //DOC accordion header click
	        $('div.ui-widget-header', v).off('click') //these needs to be here to remove click events when mobile devices scroll (which causes a screen resize event)
	            .click(function(){
		            if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
		                $(this).parent().removeClass("active");
		            }else{
		                var clickedApp = $(this).parent().parent().attr("id");
		                $("#hp-content-inner div.ui-widget.app.active").removeClass("active"); 
		                $(this).parent().addClass("active");
		                smoothScroll("#" + clickedApp, 3);
		            }
	    	    });
	    });
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
COVID-19");
		}
        }

