		//social media splitting algorithm
		var socialIconLength = $("#gb-social-media li.true").length;
		var clearClassIndex = 0;
		if( socialIconLength > 4 ){ //break on 4 if we have almost a full list
			clearClassIndex = 3;
		}else{
			if( socialIconLength >= 3){ //if 4 or 3 break list on 3
				clearClassIndex = 2;
			}else{ //else break on 2 and if only one it wont matter
				clearClassIndex = 1;
			}
		}
		$('#gb-social-media li.true:eq(' + clearClassIndex + ')').addClass('break-list');	

		if( $('#gb-footer .column.two > a img').attr('src').indexOf('default-man') >= 0 || $('#gb-footer .column.two > a img').attr('href') == "" ){
			$('#gb-footer .column.two > a').hide();
		}


		if( 'District' == 'cbh' || 'District' == 'hmr' ){
			$('#footer-phone, #footer-fax').hide();

			$('#gb-footer .column.one a')
				.click(function(e){
					e.preventDefault();
				})
				.css('cursor', 'default');
		}
	}

	function searchText() {
		var defSearchText = "Search...";

	    var searchBox = $('#gb-search input[type="text"]');

		searchBox.attr("placeholder", defSearchText);
		searchBox.focus(function() {
			if($(this).val() == defSearchText) {
				$(this).val("");
			}
		});
		searchBox.blur(function() {
			if($(this).val() == "") {
				$(this).val(defSearchText);
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}

