	/*stickyHeader
		Use: It handles the sticky header that will make the header become fixed upon scrolling
		Parameter: shutoff -- this is a numeric value in which the width of the screen will stop the sticky header from activating.
	*/

	function stickyHeader(shutoff, scroll){
		var getWidth = $(window).width();

		if (getWidth >= 1024) {
			// HEADER
			var gbHeaderOffset = $("#sticky-header-outer").offset().top;
			if ($(window).scrollTop() <= gbHeaderOffset) {
				if(csHeaderIsSticky) {
					$("#sticky-header").removeClass("on");
					$("#sticky-header-outer").css("height", "auto");

					csHeaderIsSticky = 0;
				}
			} else {
				$("#sticky-header-outer").css("height", $("#sticky-header").height());

				if(!csHeaderIsSticky) {
					$("#sticky-header").addClass("on");

					csHeaderIsSticky = 1;
				}
			}

			// CHANNEL BAR
			var gbNavOffSet = $("#sticky-channel-list").offset().top - $("#sticky-header-outer").height();
			if ($(window).scrollTop() <= gbNavOffSet) {
				if(csNavIsSticky) {
					$("#gb-channel-list-outer").removeClass("on");
					$("#sticky-channel-list").css("height", "auto");
					$("#gb-channel-list-outer").css("top", "auto");

					csNavIsSticky = 0;
				}
			} else {
				$("#sticky-channel-list").css("height", $("#gb-channel-list-outer").height());
				$("#gb-channel-list-outer").css("top", $("#sticky-header-outer").height());

				if(!csNavIsSticky) {
					$("#gb-channel-list-outer").addClass("on");

					csNavIsSticky = 1;
				}
			}

            if($(".sp").length && $(".ui-widget.app.calendar").length && csHeaderIsSticky && $(".wcm-controls").hasClass("wcm-stuck")) {
                var calStickyHeaderTopMargin = $("#sticky-header-outer").height();

                if(csNavIsSticky){
					calStickyHeaderTopMargin += $("#gb-channel-list-outer").height();
                }

                $(".wcm-controls").css("margin-top", calStickyHeaderTopMargin);
            }
		} else {
			if(csHeaderIsSticky == 1) {
				$("#sticky-header").removeClass("on");
				$("#sticky-header-outer").css("height", "auto");

				csHeaderIsSticky = 0;
			}

			if(csNavIsSticky == 1) {
				$("#gb-channel-list-outer").removeClass("on");
				$("#sticky-channel-list").css("height", "auto");
				$("#gb-channel-list-outer").css("top", "auto");

				csNavIsSticky = 0;
			}
		}
	}

	function sameHeights(objs, innerCheck, innerItems){
		var maxH = 0;

		if(innerCheck == true){
			$(innerItems[1], objs).css('min-height', 0);
			if( !$('.school-dropdown-trigger').hasClass('open-menu') ) $('#gb-schools').show();
		}else{
			objs.css('min-height', '0');
		}

		objs.each(function(k, v){
			if( $(v).outerHeight() > maxH ) maxH = $(v).outerHeight();
		});

		if(innerCheck == true){ //this will match the column heights while taking into account two rows and keeping the first row the same height.
			var maxHeaderH = 0;
			$(innerItems[0], objs).css('min-height', '0');
			objs.each(function(k, v){
				if( $(innerItems[0], v).outerHeight() > maxHeaderH ) maxHeaderH = $(innerItems[0], v).outerHeight();
			});
			$(innerItems[0], objs).css('min-height', maxHeaderH);
			$(innerItems[1], objs).css('min-height', maxH - maxHeaderH);
			if( !$('.school-dropdown-trigger').hasClass('open-menu') ) $('#gb-schools').hide();
		}else{
			objs.css('min-height', maxH);
		}
	}

