$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=q%2fqUdYICuuq837C4PuVPXw%3d%3d&SessionID=56c4d6cc-1ef7-46e5-835f-2573c01ef580&SiteID=4&ChannelID=508&SectionID=509&PageID=2347&HitDate=4%2f22%2f2020+10%3a13%3a16+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.7.117';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
