			$(".sw-mystart-button.home").hide();
		}
		//DOC #sp-breadcrumbs -- ELEMENT breadcrumbs SHOWS OR HIDES BREADCRUMBS
		var breadcrumbs = 'true';
		if(breadcrumbs == "false"){
			$("#sp-breadcrumbs").hide();
		}
		// #gb-logo -- ELEMENT showSchoolLogo SHOWS OR HIDES SCHOOL LOGO
		var showLogo = 'true'; 
		if(showLogo == "true"){
			$("#gb-logo").html('');
		}
		if($("#gb-logo img").length) {

		} else {
			$("#hp-header #gb-sitename").css("margin-top","90px");
			$("#sp-header h1#gb-name, h2#gb-tagline").css("padding-left","30px");
		}

		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();		
	}
	function loadFlash() {
		// #hp-flash-repeat -- IF #hp-flash-repeat EXISTS THEN ADD SWFObject
		if($("#hp-flash-repeat").size()){
	      	  var so = new SWFObject("/cms/lib/ND02202891/Centricity/Template/8/gb-repeat.swf","mymovie", "100%", "490", "9", "#FFFFFF");
	      	  //so.createCSS("#hp-flash-repeat","outline:none"); 
	          so.addParam("wmode", "transparent");
	          so.addVariable("primaryColor", "#000000");
	          so.write("hp-flash-repeat");
	        }
		if($("#sp-flash-repeat").size()){
		  // #sp-flash-repeat -- IF #sp-flash-repeat EXISTS THEN ADD SWFObject
	      	  var so = new SWFObject("/cms/lib/ND02202891/Centricity/Template/8/gb-repeat.swf","mymovie", "100%", "490", "9", "#FFFFFF");
	      	  //so.createCSS("#hp-flash-repeat","outline:none"); 
	          so.addParam("wmode", "transparent");
	          so.addVariable("primaryColor", "#000000");
	          so.write("sp-flash-repeat");
	        }
	}
	function hexToRgb(h){
		var r = parseInt((cutHex(h)).substring(0,2),16);
		var g = parseInt((cutHex(h)).substring(2,4),16);
		var b = parseInt((cutHex(h)).substring(4,6),16);
		return r+','+g+','+b;
	};
	function cutHex(h) {
		return (h.charAt(0)=="#") ? h.substring(1,7):h
	};

