        "Footer": function() {
         	// FOR SCOPE
            var _this = this;

            $(document).on("click keydown", "#gb-google-map-toggle", function(e) {
                if(_this.A11yClick(e)) {
                    e.preventDefault();
                    // MAP IS OPEN
                    if($("#gb-google-map").hasClass("open")) {
                        $(this).removeClass("open").text("Google Map").attr("aria-expanded", "false");					
                        $("#gb-google-map-outer").removeAttr("aria-label tabindex");                    
                        $("#gb-google-map").animate({
                            height: "0px"
                        }, 500, "linear", function() {
                            $(this).removeClass("open").hide();
                        }).attr("aria-hidden", "true");
                    }

                    // MAP IS CLOSED
                    else {
                        $(this).addClass("open").text("Close Map").attr("aria-expanded", "true");;
                        // SHOW THE MAP CONTAINER
                        $("#gb-google-map-outer").attr({
                            "aria-label": "Click To View Full Map",
                            "tabindex": "0"
                        });               
                        $("#gb-google-map").show().animate({
                            height: "200px"
                        }, 500, "linear", function() {
							$("#gb-google-map-outer").focus();
                            $(this).addClass("open");
                        }).attr("aria-hidden", "false");
                    }
                }
            });

            // GOOGLE MAP CLICK/TAB
            $(document).on("click keydown", "#gb-google-map-outer", function(e) {
            	if(_this.A11yClick(e)) {
                    e.preventDefault();              
                    var mapURL = encodeURIComponent("207 Main Avenue West West Fargo ND 58078");
                    window.open("https://www.google.com/maps/place/" + mapURL);
                }

                if(e.type == "keydown" && !e.shiftKey && e.keyCode == _this.KeyCodes.tab) {
                	e.preventDefault();                  
                    $("#gb-google-map-toggle").focus();
                }
            });

            // SHIFT+TAB FROM THE GOOGLE MAP BUTTON
            $(document).on("keydown", "#gb-google-map-toggle", function(e) {
                if(e.type == "keydown" && e.shiftKey && e.keyCode == _this.KeyCodes.tab) {
                    if($(this).attr("aria-expanded") == "true") {
                        e.preventDefault();
                        $("#gb-google-map-outer").focus();
                    }
                }
            });

            //DOC scroll to content
            $(document).on("click keydown", "#gb-scroll-top", function(e) { 
              if(_this.A11yClick(e)) {
                $("html, body").animate({
                    scrollTop: $("#gb-page").offset().top
                }, 500, function(){
                    $("html, body").stop(true, false);
                    $(".sw-channel-item:first a").focus();
                });
              } 
            });

            // MOVE Bb FOOTER STUFF
            $(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
