        "Slideshow": function() {
            if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
                $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                    "efficientLoad" : "no",
                    "imageWidth" : 800,
                    "imageHeight" : 450,
                    "appHeader" : "no",
                    "titles" : "yes",
                    "captions" : "yes",
                    "linkedElement" : ["image"],
                    "mainControls" : "yes",
                    "descMoreLink" : "yes",
                    "descMoreLinkText" : "Read More",
                    "hoverPause" : "yes",
                    "transitionType" : "slide",
                    "transitionSpeed" : 3,
                    "transitionDelay" : 8,
                    "autoRotate" : "yes",
                    "allLoaded" : function() { 
                    	$("#hp-slideshow .rs-gallery-container").append("
Archives");

                        var getWidth = $(window).width();
                        if(getWidth <= 768 && $("#sw-content-container10.region.ui-hp .rs-gallery-container").length) {
                            //DOC set height on mmr desc to keep content from jumping
                            var scrolled = 0;
                            var scrollLock = 0;
                            var loc = $("#hp-content-outer").position().top;
                            $(window).scroll(function(e) {
                                scrolled = $(window).scrollTop();
                                if(scrolled >= loc && scrollLock === 0) {
                                    $("#hp-slideshow .rs-gallery-desc").height($("#hp-slideshow .rs-gallery-desc").height());
                                    scrollLock = 1;
                                } else if(scrolled < loc && scrollLock == 1){
                                    $("#hp-slideshow .rs-gallery-desc").height("auto");
                                    scrollLock = 0;
                                }
                            });
                        }
                    }
                });
            }
        },

        "ModEvents": function() {
            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "no",
                    monthLong   : "yes",
                    dayWeek     : "yes"
                });

                var loopMonth = "";
                $(".upcomingevents .ui-article").each(function(){
                	// PUT TITLE FIRST
                	$(".ui-article-description", this).each(function() {
                    	$(this).prepend($(".sw-calendar-block-title", this));
                    });

                    // PUT ALL EVENTS FOR THE SAME DAY INTO ONE LI
                    if (!$(".ui-article-title.sw-calendar-block-date", this).length) {
                        var moveArticle = $(this).html();
                        $(this).parent().prev().find(".ui-article").append(moveArticle);
                        $(this).parent().remove();
                    } else {
                    	// GET THE DATE INTO THE DAY SPAN
                        var eventMonth = $(".joel-month", this).text();
                        $(".joel-month", this).remove();

                        var eventDay = $(".joel-day", this).text();
                        $(".joel-day", this).remove();

                        var eventDate = $.trim($(".ui-article-title.sw-calendar-block-date", this).text());
                        if(eventDate.length == 1) {
                        	eventDate = "0" + eventDate;
                        }
                        $(".ui-article-title.sw-calendar-block-date", this).empty();

                        eventDay = eventDay.match(/.{1,3}/g);

                        $(".ui-article-title.sw-calendar-block-date", this).prepend('' + eventMonth + '' + eventDate + '');
                        $(".ui-article-title.sw-calendar-block-date", this).append('' + eventDay[0] + '');

                    	// IF THIS ARTICLE IS THE START OF A MONTH
                        if(eventMonth != loopMonth) {
                        	$(this).addClass("start-month");
                            $(this).parent().prev().find(".ui-article").addClass("end-month");
                        	loopMonth = eventMonth;
                        }

                        // WRAP MONTH IN AN INNER SPAN SO THAT WE CAN HIDE THE TEXT BUT STILL OCCUPY SPACE
                        else {
                            $(".joel-month", this).removeClass("joel-month").wrap('');
                        }
                    }
                });
            }
        },

