    };
  GetContent('https://www.west-fargo.k12.nd.us//cms/UserControls/ModuleView/ModuleViewRendererWrapper.aspx?DomainID=2890&PageID=7406&ModuleInstanceID=' + MIID + '&PageModuleInstanceID=' + PMI + '&RenderLoc=0&FromRenderLoc=0&GroupYear=' + groupYear + '&GroupMonth=' + groupMonth + '&GroupByField=' + groupBy + '&EnableQuirksMode=0&ViewID=' + viewToUse + '&Tag=' + tag, container, 2, 'chkSidebar();');
}
