   function setCookie(cname, cvalue, exdays) {
     var d = new Date()
     d.setTime(d.getTime() + (exdays*24*60*60*1000))
     var expires = "expires="+d.toUTCString()
    document.cookie = cname + "=" + cvalue + "; " + expires;
   }

   function getCookie(cname) {
     var name = cname + "="
     var ca = document.cookie.split(';')
     for(var i=0; i<ca.length; i++) {
       var c = ca[i]
       while (c.charAt(0)==' ') c = c.substring(1)
       if (c.indexOf(name) == 0) return c.substring(name.length,c.length)
     }
     return ""
   }

   function showPopup() {
     setCookie('cpsshown', 'true', 365)
     //document.querySelector('#letter').style.display = 'block'
     jQuery("#letter").hide(0).delay(1000).fadeIn('slow')
   }

   jQuery(document).ready(function(){
    var cookie = getCookie('cpsshown')
    if (!cookie) {
      showPopup()
    }
   })

jQuery(document).ready(function () {
  var options = { 
     autoPlay: true,
     autoPlayDelay: 6000,
     pauseOnHover: true,
     hidePreloaderDelay: 500,
     preloader: true,
     hidePreloaderUsingCSS: false,    
     animateStartingFrameIn: true,    
     navigationSkipThreshold: 750,
     preventDelayWhenReversingAnimations: true,
     pagination:true,
     customKeyEvents: {
         80: "pause"
     } ,
    keyEvents: {
      left: "prev", 
      right: "next"
    }
   };  

  var cpssequence = jQuery("#sequence").sequence(options).data("sequence");

  cpssequence.afterNextFrameAnimatesIn = function() {
   jQuery("#bannernav div ul li").each( function () {
    jQuery(this).removeClass("selected");
   });

   jQuery("#bannernav div ul li#slider-nav-" + cpssequence.currentFrameID).addClass("selected");
  }

  jQuery("#bannernav div ul li").click( function () {
   var toloc = jQuery(this).attr("id");
   toloc = toloc.split("-");

   cpssequence.goTo(toloc[2]);
  });
});

