jQuery(document).ready(function () {
 jQuery('#openform').keyup(function () {
  let keycode = (window.event) ? event.keyCode : e.keyCode;

  if (keycode == 13) {
   jQuery('#openform').trigger('click')
  }
 })

 jQuery('#openform').click(function () {
  jQuery('#formbox').toggle('slow')
  jQuery('span.plusminus').text() == '+' ? jQuery('span.plusminus').text('-') : jQuery('span.plusminus').text('+');
  jQuery('span.instruct').text() == 'Click to Open the Form' ? jQuery('span.instruct').text('Click to Close the Form') : jQuery('span.instruct').text('Click to Open the Form');
 })
})

