				$('#newsTickerContent').addClass('tickerImpShow').removeClass('tickerImpHide');
				expanded = true;
			}
		});

		$(function () {
			var items = $('#newsTickerContent li');
			var index = 0;
			var tickerTimeout = null;
			var paused = false;
			var pausedByButton = false;

            tickerCycle();

            $('#tickerPlayPause').on('click', function () {
                if ($(this).hasClass('pause')) {
                    $(this).find('.obscure').text('resume the news ticker');
                    $(this).addClass('play');
                    $(this).removeClass('pause');
                    paused = true;
                    pausedByButton = true;
                } else {
                    $(this).find('.obscure').text('pause the news ticker');
                    $(this).addClass('pause');
                    $(this).removeClass('play');
                    paused = false;
                    pausedByButton = false;

                    tickerCycle();
                }
            });

			$('#newsTicker').hover(function () {
				paused = true;
				if (tickerTimeout) {
					window.clearTimeout(tickerTimeout);
					tickerTimeout = null;
				}
			}, function () {
				if (!pausedByButton && (paused && !tickerTimeout)) {
					tickerTimeout = window.setTimeout(function () {
						paused = false;
						tickerCycle();
					}, 3000);
				}
			});

			function tickerCycle() {
				var target = $('#newsTickerInner');
				var startOffset = 1024;
				var item = items[index];
				if (++index >= items.length) index = 0;

				removeCurrentTickerItem(function () {
					var tickerItemWrapper = $("
")
						.addClass("tickerItemWrapper")
						.css('left', startOffset + 'px');
					$("
")
						.addClass("tickerItem")
						.html($(item).html())
						.appendTo(tickerItemWrapper);
					$(tickerItemWrapper).appendTo(target);

					var targetOffset = '0px';
					$(tickerItemWrapper).animate({ left: targetOffset }, 1500, 'easeOutCirc', function () {
						if (!paused) {
							tickerTimeout = window.setTimeout(function () {
								tickerCycle();
								tickerTimeout = null;
							}, 3000);
						}
					});
				});
			}

			function removeCurrentTickerItem(callback) {
				if ($('#newsTickerInner .tickerItemWrapper').length > 0) {
					var targetOffset = '-1024px';
					$('#newsTickerInner .tickerItemWrapper').animate({ left: targetOffset }, 1500, 'easeOutCirc', function () {
						$(this).remove();
					});
					callback();
				} else {
					callback();
				}
			}
		});

    function getUrlParameter(name) {
        name = name.replace(/[\[]/, '\\[').replace(/[\]]/, '\\]');
        var regex = new RegExp('[\\?&]' + name + '=([^&#]*)');
        var results1 = regex.exec(location.search);
        var returnVal = (results1 === null) ? '' : decodeURIComponent(results[1].replace(/\+/g, ' '));

        if (returnVal === '') {
            var results2 = regex.exec(location.hash);
            returnVal = (results2 === null) ? '' : decodeURIComponent(results2[1].replace(/\+/g, ' '));
        }

        return returnVal;
    }

	function googleTranslateElementInitMobile() {
        new google.translate.TranslateElement({ pageLanguage: 'en',  layout: google.translate.TranslateElement.InlineLayout.Mobile }, 'google_translate_element');

        var lang = getUrlParameter('translatelang');

        if (lang != '') {
            var jObj = $('#googleTranslateSelect');
            var db = jObj.get(0);
            jObj.val(lang);
            fireEvent(db, 'change');
        }
    }

    function fireEvent(element, event) {
        if (document.createEventObject) {
            // dispatch for IE
            var evt = document.createEventObject();
            return document.getElementById("googleTranslateSelect").fireEvent('on' + event, evt);
        }
        else {
            // dispatch for firefox + others
            var evt = document.createEvent("HTMLEvents");
            evt.initEvent(event, true, true);
            return !element.dispatchEvent(evt);
        }
    }

