link.script:
    var nav = $("#desktop_top_nav");
    var breakpoint = $("#topnav_fix_breakpoint");
    var navHeight = nav.outerHeight();

    function positionNav() {
        var windowTop = $(window).scrollTop();
        var navTop = breakpoint.offset().top;

        if (windowTop >= navTop) {
            nav.addClass('fixed-nav');
            breakpoint.height(navHeight);
        } else {
            nav.removeClass('fixed-nav');
            breakpoint.height(0);
        }
    }

    $(window).bind('scroll', function () {
        positionNav();
    });

    $(window).resize(function () {
        positionNav();
    }).resize();

    // Add a spacing element to the middle of the dynamic top nav
    $("
").insertAfter("#navigation li.nav_item:nth-child(3)");

    // Combine the two top navs and remove the header logo/spacing element
    var combinedMenu = $("#static_nav").clone();

    var schoolHome = combinedMenu.children("li.staticnav_logo").clone();
    schoolHome.children("a").empty().text("School Homepage");

    combinedMenu.children("li.staticnav_logo").remove();
    var topNav = $("#navigation").clone();
    topNav.children(".nav_spacer").remove();

    schoolHome.appendTo(combinedMenu);
    topNav.children('li').appendTo(combinedMenu);

    function fixParallax() {
        jQuery(window).trigger('resize').trigger('scroll');
    }

    // Slicknav the combined nav
    var sitenav = combinedMenu.slicknav({
        label: 'Menu',
        prependTo: '#mobile_top_nav',
        allowParentLinks: true,
        removeClasses: true,
        duplicate: false,
        brand: '
Home',
        afterOpen: fixParallax,
        afterClose: fixParallax
    });

