$(document).ready(function(){
  
  $('.m_link').each(function(){
    
    var $this = $(this);
    
    if($this.attr('href').indexOf('javascript:var w=window.open') == 0)
      {
        //$this.attr('data-href',$this.attr('href'));
        
        var hrefReg = /window.open\('([^']+)/;
        
        //console.log($this.attr('href').match(hrefReg));
        
        var hrefMatch = $this.attr('href').match(hrefReg);
        
        $this.attr('target','_blank');
        $this.attr('href',hrefMatch[1]);
      }
    
  });
  
});

