link.script:
    var topNav = $('#navigation');
    var mobileButton = $('#mobile-nav-button');

    topNav.slicknav({
        label: '',
        prependTo: '#mobile-top-nav',
        allowParentLinks: true,
        removeClasses: true
    });

    mobileButton.on("click", function () {
        topNav.slicknav('toggle');
    });

    // Search Panel
    const searchPanel = $("#ucTopNav_pnlSearch");
    const searchToggle = $("#search-toggle");
    const searchClose = $("#search-close");
    const searchBox = $("#ucTopNav_txtSearch");

    searchToggle.click(function () {
        searchPanel.addClass("togglebox__visible");
        searchBox.focus();
    });

    searchClose.click(function () {
        searchPanel.removeClass("togglebox__visible");
        searchBox.blur();
    });

input.script:
    $(document).ready(function () {
        var obj = $(".current_page");
        var searching = true;
        if (obj.length > 0) {
            while (searching) {
                obj = $(obj).parent();
                var arrow = $(obj).find("> a > .slicknav_arrow");
                if (arrow.length > 0) {
                    $(arrow).click();
                }
                if ($(obj).attr("class") == "slicknav_menu") {
                    searching = false;
                }
            }
        }
    });

    var hometext = $("#ucLeftNav_ctl00_lnkTopHome").text();

    //Slicknav
    $('#ul_dynamic_nav').slicknav({
        label: hometext,
        allowParentLinks: true,
        prependTo: "#dynamic_nav",
        removeIds: true,
        removeClasses: false,
        duplicate: false,
        closedSymbol: "",
        openedSymbol: ""
    });
