div.script:
$(document).ready(function () {
    $("#8f5582d4-144d-466b-a8b4-e2069c905a8c").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 6000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#97f7b4d8-b4b2-4515-a590-db3a8d8a6f17").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#97f7b4d8-b4b2-4515-a590-db3a8d8a6f17').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#97f7b4d8-b4b2-4515-a590-db3a8d8a6f17').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 10000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#a18ee48a-4332-4280-ae74-421f423db0b9').hide();
    jQuery('#a18ee48a-4332-4280-ae74-421f423db0b9').listSolrResults({
        currentPagePath: '/content/schools/ohva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: 'a18ee48a-4332-4280-ae74-421f423db0b9',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

