    $(document).on('cycle-initialized', function (event, optionHash, outgoingSlideEl, incomingSlideEl, forwardFlag) {
        var youtubeSlide = $('#slideshow .cycle-slide-active').children('.youtubeVideoSlide');
        var vimeoSlide = $('#slideshow .cycle-slide-active').children('.vimeoVideoSlide');

        if (youtubeSlide.length > 0 && youtubeSlide.data('autoplay') == true) {
            youtubeSlide.parents('.headerGalleryV2').cycle('pause');
        }

        if (vimeoSlide.length > 0 && vimeoSlide.data('autoplay') == true) {
            vimeoSlide.parents('.headerGalleryV2').cycle('pause');
            playVimeoPlayerById(vimeoSlide.attr('id'));
        }

        $('.cycle-pager > span').attr('tabindex', 0);
    });

