	if (typeof ga === "undefined") {
		(function (i, s, o, g, r, a, m) {
			i['GoogleAnalyticsObject'] = r; i[r] = i[r] || function () {
				(i[r].q = i[r].q || []).push(arguments)
			}, i[r].l = 1 * new Date(); a = s.createElement(o),
				m = s.getElementsByTagName(o)[0]; a.async = 1; a.src = g; m.parentNode.insertBefore(a, m)
		})(window, document, 'script', '//www.google-analytics.com/analytics.js', 'ga');
	}

	
	ga('create', 'UA-61700231-46', 'auto', { 'name': 'SchoolPointeTracker0' });
	ga('SchoolPointeTracker0.send', 'pageview');
	

    /*
	* Function that tracks a click on an outbound link in Analytics.
	* This function takes a valid URL string as an argument, and uses that URL string
	* as the event label. Setting the transport method to 'beacon' lets the hit be sent
	* using 'navigator.sendBeacon' in browser that support it.
	*/

	var trackOutboundLink = function (url) {
		ga('send', 'event', 'outbound', 'click', url, {
			'transport': 'beacon',
			'hitCallback': function () { document.location = url; }
		});
	}

body.script:
        $(document).ready(function () {
            $('a').on('click', function () {
                var href = $(this).attr('href');

                if (href.startsWith('/') || href.startsWith('#') || href.startsWith('javascript:')) {
                    return;
                }

                if (href.startsWith('http')) {
                    href = href.substring(href.indexOf('://') + 3, href.length - 1);
                }

                if (href.startsWith('www.')) {
                    href = href.substring(4, href.length - 1);
                }

                if (href.startsWith('polandbulldogs.com') || href.startsWith('poland.k12.oh.us') || href.startsWith('polandlclschs.oh.schools.bz')) {
                    return;
                }

                var x = window.confirm("You are about to leave Poland Local Schools' website. This link is not under the control of the Poland Local Schools, and is not responsible for the contents of any linked site or any link contained in a linked site, or any changes or updates to such sites. We are providing this link to you only as a convenience, and the inclusion of any link does not imply endorsement of the site by the district. Are you sure you want to leave?")
                return x;
            });

            $('.cycle-pager > span').attr('tabindex', 0);
        });

    $(document).ready(function () {
        $('body').on('keyup', function (e) {
            var keyCode = e.keyCode || e.which;

            if (keyCode == 9) {
                var $focusedParents = $(':focus').parents('.dropdown.open');
                var $openMenus = $('.dropdown.open');

                if ($focusedParents.length < $openMenus.length) {
                    $openMenus.not($focusedParents).removeClass('open');
                }
            }
        });
    });
