function waitForElement() {
    if (typeof videojs !== "undefined") {
        videojs.options.flash.swf = "//vjs.zencdn.net/5.8.0/video-js.swf";
        videojs('video', {}, function () {
            videojs.ga();
        });
    }
    else {
        setTimeout(function () {
            waitForElement();
        }, 1);
    }
}

