    function UASuperCode() {
        var loc = ".";
        if (window.location.href.includes("/protected/")) {
            loc = "..";
        }
        var ranN = Math.floor(Math.random() * fSImgURLs.length);
        $("[src]").attr("src", loc + fSImgURLs[ranN]);
