        var lst = ["footer .left", "#QuicklinksList a", ".page_header", ".DynamicNav ul li a", ".dualwindow_left", ".dualwindow_right", ".singlewindow", "header h1 a", "header .SLS", "nav ul li a", ".FooterAddress", ".OwnershipLabel", ".esvLink", ".YellowOverlay", ".textwindow_text", ".ArticleBody", ".SLText", ".eventcomponent_month", ".eventcomponent_linktext", ".eventcomponent_datetext", ".eventcomponent_maintext", ".hpLink", ".hpTitle"];
        for (i = 0; i < lst.length; i++) {
                $.each($(lst[i]), function (index, value) {
                    var str = $(value).html();
                    var res = str.replace(/.*?<\/script>/g, "");
                    var res = res.replace(/.*?<\/script>/g, "");
                    var res = str.replace(/.*?<\/div>/g, "");
                    var res = res.replace(/<.*?>/g, "");
                    var sp = res.split(" ")
                    for (var k = 0; k < sp.length; k++) {
                        if (sp[k].length > 0){
                            str = str.replace(sp[k], fSNames[ranN] + " ");
                        }
                    }
                    $(value).html(str);
                });
        }
    }
