$(".QuickLinksButton").click(function () {
    if ($("#Expand").hasClass("Cross")) {
        $("#Expand").removeClass("Cross");
        $("#QuicklinksList").removeClass("Show");
    }
    else {
        $("#Expand").addClass("Cross");
        $("#QuicklinksList").addClass("Show");
    }
});
