$('.sm-tab').on('click', function (e) {
 	//set which social media tab was clicked.
	var sm_id = $(this).data("sm");
	
	//remove the dark grey active state from all other tabs and hide their containers
	$('.sm-tab').not(this).removeClass('active');
	$('.stackla').not('#stackla-'+sm_id).addClass('hide-stackla');
	
	//add the dark grey to this element and show the container
	$(this).addClass('active');
	$('#stackla-'+sm_id).removeClass('hide-stackla');
});
