function pageLoad(actionable, newTitle) {
        var analyticsInterval = window.setInterval(function() {
            if (newTitle){
                document.querySelector('meta[name="title"]').setAttribute("content", newTitle);
                window.marketingDigitalData.page.pageInfo.pageTitle = newTitle;
            }
            if (window.triggerEvent && window.PAGE_LOAD_EVENT && window.digitalData && typeof window.digitalData.hasCallbacks == "function" && window.digitalData.hasCallbacks() > 0) {
              if(!window.marketingDigitalData.page.pageInfo.icidList){
                  window.marketingDigitalData.page.pageInfo.icidList = [];
              }
                if (!window.pageLoadEventHasFired) {
                    window.pageLoadEventHasFired = false;
                }

                window.marketingDigitalData.page.pageInfo.icidList = [];
                angular.forEach(window.marketingDigitalData.page.modules.moduleListAll, function(module) {
                    angular.element('[data-analytics-id="' + module.ID + '"]').attr('data-placement-id', module.placementID);
                    angular.forEach(module.actionableElements, function(element) {
                       if (element.icid) {
                            window.marketingDigitalData.page.pageInfo.icidList.push(element.icid);
                       }
                    });
                });

                var modals = document.querySelectorAll('[data-ui-mode="dialog"]');
                var modalIcids = [];

                _.forEach(modals, function(modalSection) { 
                    var modalAnalytics = modalSection.querySelector('[data-analytics-config]');
                    var modalData = JSON.parse(modalAnalytics.getAttribute('data-analytics-config'));

                     _.forEach(modalData.actionableElements, function(actionableElements) {
                        var icid = actionableElements.icid;

                        modalIcids.push(icid);
                    });
                });   
                var isPagePicker;
                var hasModulePicker;
                var uniqModalIcids = _.uniq(modalIcids);
                var allPageIcids = _.uniq(window.marketingDigitalData.page.pageInfo.icidList);
                var pageLoadIcids = _.difference(allPageIcids, uniqModalIcids);
                var evt = {};
                evt.attributes = {};
                evt.eventInfo = {};
                evt.attributes = {};
                evt.eventDescription = {};
                evt.eventInfo.eventAction = "load";
                evt.eventInfo.eventType = "page";
                evt.eventInfo.timeStamp = new Date().toString();
                if(!window.modPickICID){
                    window.modPickICID = [];
                }
                window.modPickICID = [];
                window.modPickerCount = 0;
                window.modPickerSuccess = 0;
                window.marketingDigitalData.page.pageInfo.icidList = _.uniq(window.marketingDigitalData.page.pageInfo.icidList);
                window.marketingDigitalData.page.pageInfo.timeStamp = new Date().toString();
                if (window.marketingConfig.hasPersonalizedPage) {
                    if (window.marketingDigitalData.page.pageInfo.icidList.length) {
                        evt.attributes = {};
                        evt.attributes.icidList = _.uniq(pageLoadIcids);
                    }
                    evt.eventDescription.type = "page picker";
                }
                else if (window.marketingConfig.hasPersonalizedModule) {
                    if (window.marketingDigitalData.page.pageInfo.icidList.length) {
                        evt.attributes = {};
                        evt.attributes.icidList = _.uniq(pageLoadIcids);
                    }
                    evt.eventDescription.type = "module picker";
                }
                else {
                    if (window.marketingDigitalData.page.pageInfo.icidList.length) {
                        evt.attributes = {};
                        evt.attributes.icidList = _.uniq(pageLoadIcids);
                    }
                    evt.eventDescription.type = "default page";
                }
                angular.merge(window.digitalData, window.marketingDigitalData);
                window.digitalData.events.push(evt);
                window.triggerEvent(window.PAGE_LOAD_EVENT);
                window.clearInterval(analyticsInterval);
                window.pageLoadEventHasFired = true;
            }
        }, 100);
    }

