p.script:
$(document).ready(function(){
    $("button").click(function(){
        $(".tab-pane").toggleClass("active");
        $(".in").toggleClass("active");
        $(".tab-pane").toggleClass("printspacing");
    });

  var id = location.hash;
  var clid = location.hash.substr(1);
  var pageid = location.search.slice(1).split("=")[1];
  if ( id ) { $('.nav-tabs a[href$="#' + clid + '"]').parent().addClass('active'); };
  if (pageid === "remote-students") { window.location.hash = '#remote'; $('.nav-tabs a[href$="#remote"]').parent().addClass('active'); };
});

$(function () {
  $('[data-toggle="tooltip"]').tooltip();
})

