$(document).ready(function() {
    // run test on initial page load
    handleScreenSize();

    // run test on resize of the window
    $(window).resize(handleScreenSize);
    
    $('.top-bar p').click(doClickAction);
    
});

//Function to the css rule
function checkSize(){
    if ($("#NAV").css("float") == "none" ){
        return true;
    } else {
        return false;
    }
}

function handleScreenSize() {
    if (checkSize()) {
        handleSmallScreen();
    } else {
        handleLargeScreen();
    }
}

function handleSmallScreen() {
    $('.top-bar ul').hide();
    $('.top-bar').prependTo('#MPheader > .row:first-child > .col-md-12');
    $('.search-field-wrapper').prependTo('ul.navbar-nav');
}

function handleLargeScreen() {
    $('.top-bar ul').css('display','inline-block');
    $('.top-bar').insertBefore('#MPimage');
    $('.search-field-wrapper').insertAfter('#MPAuxNav'); 
}

function doClickAction() {
    if (checkSize()) {
        $('.top-bar ul').toggle('fast');
        $('.top-bar ul').css('display','block');
    } else {
        return;
    }
}

;(function($){
  
  /**
   * jQuery function to prevent default anchor event and take the href * and the title to make a share popup
   *
   * @param  {[object]} e           [Mouse event]
   * @param  {[integer]} intWidth   [Popup width defalut 500]
   * @param  {[integer]} intHeight  [Popup height defalut 400]
   * @param  {[boolean]} blnResize  [Is popup resizeabel default true]
   */
  $.fn.customerPopup = function (e, intWidth, intHeight, blnResize) {
    
    // Prevent default anchor event
    e.preventDefault();
    
    // Set values for window
    intWidth = intWidth || '500';
    intHeight = intHeight || '400';
    strResize = (blnResize ? 'yes' : 'no');

    // Set title and open popup with focus on it
    var strTitle = ((typeof this.attr('title') !== 'undefined') ? this.attr('title') : 'Share'),
     strParam = 'width=' + intWidth + ',height=' + intHeight + ',resizable=' + strResize,            
      objWindow = window.open(this.attr('href'), strTitle, strParam).focus();
  }
  
  /* ================================================== */
  
