    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.DynamicStyleSheet();
            this.Header();
            this.MyStart();            
            this.Body();
            this.UpcomingEvents();
            this.Footer();
            this.Slideshow();
            this.StreamingVideo();
            this.AppAccordion();
            this.Search();
            this.AnimateScrollButton();

            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });

        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.ChannelBar();

           	// REMOVE TAB INDEXES FROM SYSTEM  TAGS
            $(".sw-channel-item, li.sw-channel-item a, ul.sw-channel-dropdown li, ul.sw-channel-dropdown li a").off().unbind().removeAttr("tabindex");

            this.JsMediaQueries();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
        },

        "JsMediaQueries": function() {
        	//For scopr
            var _this = this;

            switch(this.GetBreakPoint()) {
                case "desktop": 
                    this.ChannelAccordion(true);
                    $(".hp-row.one .headlines").each(function() {
                    	_this.ResetAppBulletSlider(this, 6);
                        _this.AppBulletSlider(this, 6);
                    });
                    $(".hp-row.two .headlines").each(function() {
                    	_this.ResetAppBulletSlider(this, 0);
                    });
                break;
                case "768":
                case "640":
                case "480":
                    this.ChannelAccordion(false);
                    $(".hp-row.one .headlines").each(function() {
                   	 	_this.ResetAppBulletSlider(this, 6);
                        _this.AppBulletSlider(this, 6);
                    });
                    $(".hp-row.two .headlines").each(function() {
                   	 	_this.ResetAppBulletSlider(this, 4);                    
                    	_this.AppBulletSlider(this, 4);
                    });
                break;
				case "320":
                    this.ChannelAccordion(false);
                    $(".hp-row.one .headlines").each(function() {
                   	 	_this.ResetAppBulletSlider(this, 4);
                        _this.AppBulletSlider(this, 4);
                    });
                    $(".hp-row.two .headlines").each(function() {
                   	 	_this.ResetAppBulletSlider(this, 2);                    
                    	_this.AppBulletSlider(this, 2);
                    });    
                break;
            }
        },

        "DynamicStyleSheet": function() {
        	//Variables
            var mainFixedBGSrc = $.trim('/cms/lib/OK01000367/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg');
            var regionEBGSrc = $.trim('/cms/lib/OK01000367/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg');
            var regionFBGSrc = $.trim('/cms/lib/OK01000367/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg');
            var srcCheck = mainFixedBGSrc.indexOf("Faces/default-man.jpg") > -1;
            var srcCheck2 = regionEBGSrc.indexOf("Faces/default-man.jpg") > -1;
            var srcCheck3 = regionFBGSrc.indexOf("Faces/default-man.jpg") > -1;

            var styles = '.hp #gb-header-outer {\n'+
                            '\t box-shadow: 0 0 0 10px rgba('+this.HexToRgb('#003D5C')+', 0.4);\n'+
                         '}\n'+
                         '.hp #gb-header-outer:hover, .hp #gb-header-outer:focus {\n'+
                            '\t box-shadow: 0 0 0 20px rgba('+this.HexToRgb('#003D5C')+', 0.4);\n'+
                         '}\n'+
                         '.hp #gb-flyout-menu-button, .hp #gb-flyout-menu-button-close, #hp-slideshow .mmg-description-link.watch-video, a.more-link .cs-dev-icons, .cs-article-info-close {\n'+
                            '\t box-shadow: 0 0 0 5px rgba('+this.HexToRgb('#003D5C')+', 0.7);\n'+
                         '}\n'+
                         '.hp #gb-flyout-menu-button:hover, .hp #gb-flyout-menu-button:focus, .hp #gb-flyout-menu-button-close:hover, .hp #gb-flyout-menu-button-close:focus, \n'+
                         '#hp-slideshow .mmg-description-link.watch-video:hover, #hp-slideshow .mmg-description-link.watch-video:focus {\n'+
                            '\t box-shadow: 0 0 0 9px rgba('+this.HexToRgb('#003D5C')+', 0.7);\n'+
