					} else {
                    	$("#gb-logo").append('');
                    }
                }
            }

            // ADD SITENAME
            var siteNameOne = $.trim("");
            var siteNameTwo = $.trim("");
            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Moore Public Schools";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
            }

            $(document).on("click keydown", "#gb-flyout-menu-button", function(e) { 
                if(_this.AllyClick(e)) {
					if($(this).attr("aria-expanded") == "false" && !$("#gb-flyout-menu-outer").hasClass("animating")) {
                    	$("#gb-flyout-menu-outer").addClass("animating");
                        $(this).attr("aria-expanded", "true");
                        $("#gb-flyout-menu-button-close").attr("aria-expanded", "true");
                        $("#gb-flyout-menu-outer").attr("aria-hidden", "false").fadeIn(250);
                        $("#gb-flyout-menus").addClass("open");

                        //Close search and hiden search button
                        $("#gb-search-menu-button").attr("aria-expanded", "false").fadeOut(250);
					 	$("#gb-search-form").attr("aria-hidden", "true")
                        $("#gb-search-form-outer").hide("slide", { direction: "right" }, 250);

                        setTimeout(function() {
                        	$("#gb-flyout-menu-button").hide();
                            $(".gb-flyout-menu-row.one").find(":focusable").first().focus();
                            $("#gb-flyout-menu-outer").removeClass("animating");
                        }, 260);
                    }
                }
            });

            $(document).on("click keydown", "#gb-flyout-menu-button-close", function(e) { 
                if(_this.AllyClick(e)) {
					if($(this).attr("aria-expanded") == "true" && !$("#gb-flyout-menu-outer").hasClass("animating")) {
                    	$("#gb-flyout-menu-outer").addClass("animating");
                        $(this).attr("aria-expanded", "false");
                        $("#gb-flyout-menu-button").show().attr("aria-expanded", "false");
                        $("#gb-flyout-menu-outer").attr("aria-hidden", "true").fadeOut(250);
                        $("#gb-flyout-menus").removeClass("open");
                        $("#gb-search-menu-button").fadeIn(250, function() { $("#gb-flyout-menu-outer").removeClass("animating"); });
                    }
                } else if(e.keyCode == _this.KeyCodes.tab) {
                    if(e.shiftKey) {
                        e.preventDefault();
                        $(".gb-flyout-menu-row.two").find(":focusable").last().focus();
                    } else {
                        e.preventDefault();
                        $(".gb-flyout-menu-row.one").find(":focusable").first().focus();
                    }
                }
            });

            $(document).on("keydown", ".gb-school-lists a", function(e) { 
                if(e.keyCode == _this.KeyCodes.tab && $("#gb-flyout-menu a").index(this) == $("#gb-flyout-menu a").length-1) {
                    e.preventDefault();
                    $("#gb-flyout-menu-button-close").focus();
                }
            });  
        },

