        "AppBulletSlider": function(app, slideNumber) {
        	//For scope
            var _this = this;

        	//Variables
        	var totalRecords = $(".ui-article", app).length;
            var bulletCount = Math.ceil(totalRecords/slideNumber);
            var counter = 0;

            if(totalRecords > slideNumber) {
                var structure = '
'+
                                  '';
                              while(counter < bulletCount ) {
                              	var label = "Navigate to Slide "+(counter+1);

                              	if(counter == 0) {
         				structure += '

';
								} else {
                        structure += '

';
                                }
                                  counter++;
                              }
                    structure += ''+
                                '';
                $(".ui-widget-footer", app).prepend(structure);

                var records = $(".ui-articles > li", app);
                for(var i = slideNumber; i < records.length; i+=slideNumber) {
                    records.slice(i, i+slideNumber).wrapAll('');
                }

                $(".ui-articles", app).prependTo($(".ui-widget-detail", app));
                $(".ui-widget-detail", app).wrapInner('

');
                $(".ui-articles", app).eq(0).addClass("active");

                $(".ui-articles a", app).attr("tabindex", -1);
                $(".ui-articles.active a", app).attr("tabindex", 0);
            }

            $(".cs-article-bullet", app).on("click keydown", function(e) {
            	if(_this.AllyClick(e)) {
                	$(".cs-article-bullet", app).each(function() {
                    	$(this).removeClass("active").attr("aria-label", "Navigate to Slide "+$(this).attr("data-index"));
                    });

                    $(this).addClass("active").attr("aria-label", "Slide "+$(this).attr("data-index")+": Current Item");

                	var index = $(this).parent().index();
                    var pos = index;
                    var sliderWidth = $(".cs-widget-detail-inner", app).width()

                    $(".cs-widget-detail-inner", app).css("transform", "translate3d(" + (-pos * sliderWidth) + "px,0,0)");

                    $(".ui-articles", app).removeClass("active");
                    $(".ui-articles", app).eq($(this).parent().index()).addClass("active");

                    $(".ui-articles a", app).attr("tabindex", -1);
                    $(".ui-articles.active a", app).attr("tabindex", 0);

                    setTimeout(function() {
                    	$(".ui-articles.active", app).find(":focusable").first().focus();
                    }, 1010);
                }
            })
        },

        "ResetAppBulletSlider": function(app, slideNumber) {
        	if($(".cs-articles-bullets", app).length) {
                if(slideNumber == 0) {
                    $(".ui-widget-detail", app).prepend('');
                    $(".ui-articles li", app).appendTo($(".ui-widget-detail > .ui-articles", app));
                    $(".cs-widget-detail, .cs-articles-bullets", app).remove();            
                } else {
                    var totalRecords = $(".ui-article", app).length;
                    var bulletCount = Math.ceil(totalRecords/slideNumber);
                    var bulletLength = $(".cs-articles-bullet", app).length;

                    if(bulletCount != bulletLength) {
                        $(".ui-widget-detail", app).prepend('');
                        $(".ui-articles li", app).appendTo($(".ui-widget-detail > .ui-articles", app));
                        $(".cs-widget-detail, .cs-articles-bullets", app).remove();
                    }
                }
            }
        },

        "HeadlinesBox": function(app) {
            // FOR SCOPE
            var _this = this;

            $(".ui-articles", app).attr("role", "tablist");
            $(".ui-articles .ui-article-title, .ui-articles .ui-article-description", app).attr("aria-hidden", true);
            $(".ui-articles li:eq(0) .ui-article .ui-article-thumb", app).attr("aria-selected", true);            

            var structure = '
';

            $(".ui-article", app).each(function() {
                $(".ui-article-thumb", this).attr({"tabindex": 0, "aria-controls": "hp-tab-content-"+$(this).parent().index(), "aria-selected": true, "role": "tab", "data-index": $(this).parent().index() });
                $(".ui-article-title a", this).parent().attr("data-url", $(".ui-article-title a", this).attr("href")).html($(".ui-article-title a", this).html());

                var attrs = { };

                $.each($(".ui-article-thumb", this)[0].attributes, function(idx, attr) {
                    attrs[attr.nodeName] = attr.nodeValue;
                });


                $(".ui-article-thumb", this).replaceWith(function () {
                    return $("", attrs).append($(this).contents());
                });

                $(".ui-article-thumb", this).removeAttr("href");

                if($(this).parent().index() == 0) { 
               		 structure += '
';
                } else {
               		 structure += '
';
                }
                structure += 	  	'
'+  
                 						'
'+
		 		 						'
'+$.trim($(".ui-article-title span", this).text())+'';
                       if($(".ui-article-description", this).text() != "") {
                           structure += '
'+$(".ui-article-description", this).text()+'';
                       }
                structure += 			'
More'+   
                                     ''+
               					  '';
            });

            structure += '';

            $(".ui-widget-detail", app).append(structure);

            $(".ui-article-thumb", app).on("click keydown", function(e) {            
                if(_this.AllyClick(e)) {
                	e.preventDefault();
                    var index = $(this).attr("data-index");

                    if(!$(".cs-article-info-inner.transition", app).length) {
                      $(".ui-article .ui-article-thumb", app).attr("aria-selected", false);
                      $(this).attr("aria-selected", true);

                      $('.cs-article-info[aria-hidden="false"]', app).addClass("transition").fadeOut(250, function() {
                      	  $(".cs-article-info-outer").show();
                          $(".cs-article-info", app).attr("aria-hidden", true);
                          $(".cs-article-info[data-index='"+index+"']", app).attr("aria-hidden", false);

                          $('.cs-article-info[aria-hidden="false"]', app).fadeIn(250, function() {
                          	$(".cs-article-info", app).removeClass("transition");
                            $('.cs-article-info[aria-hidden="false"]', app).find(":focusable").first().focus();
                          });
                      });
                    } else if($(this).parent().hasClass("active") && !$(".cs-article-info-inner.transition", app).length) {
                    	$(".cs-article-info-inner", app).find(":focusable").first().focus();
                    }
                }
            });

            $(".cs-article-info-close", app).on("click keydown", function(e) {            
                if(_this.AllyClick(e)) {
                	e.preventDefault();                    
					$(".cs-article-info-outer", app).addClass("transition").fadeOut(250, function() {
                        $(".cs-article-info-inner", app).removeClass("transition");
                    });
                }
            });
        },        

        "UpcomingEvents": function() {
            // FOR SCOPE
            var _this = this;

            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "yes",
                    dayWeek     : "no"
                });

                $(".upcomingevents .ui-article").each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                    	//Variables
                        var upcomingColumnLeft = $(this).parent().prev().find(".upcoming-column.left").html();

                       	$(this).addClass("upcoming-column-container");
                        $(this).prepend('
'+upcomingColumnLeft+'');
                        $(".ui-article-description", this).wrap('
');
                    };

					if($(".hp").length) {
						$(this).append('Read more about '+$.trim($(".sw-calendar-block-title", this).text())+'.');
                	}
                    $(".sw-calendar-block-title", this).text($(".sw-calendar-block-title", this).text());
                });

                $(".sw-calendar-block-date").each(function() {
                	var month = $(".joel-month", this);
                    $(".joel-month", this).remove();

                    if($.trim($(this).text()).length == 1) {
                    	$(this).html("0"+$(this).text());
                    }
                    $(this).prepend(month);
                });

                eventsByDay(".sp .upcomingevents .ui-articles");
            }

            function eventsByDay(container) {
                var countLi = 0;
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };

                });
            }
        },

