');
                            }

                        	mmgPauseBtn = $(".mmg-control.play-pause", props.element);
                            mmgPauseBtn.appendTo($(".mmg-container > .mmg-description-outer .mmg-description-links", props.element));
                        }

                        mmgPauseBtn.on("click", function() {
