        "AnimateScrollButton": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var position = 358 + $("#gb-social-media").outerHeight();

                    	$(window).scroll(function() {
                            var scrollHeight = $(".gb-content").position().top-5;
                            var scrollPosition = $(window).scrollTop();

                            if(scrollPosition > scrollHeight) {
                                $("#gb-scroll-button").addClass("cs-scroll-up").css("top", position);
                            } else {
                                $("#gb-scroll-button").removeClass("cs-scroll-up").removeAttr("style");
                            }
                        });
                    break;
                }
            }
        },

