        "Search": function() {
        	// FOR SCOPE
			var _this = this;

            $("#gb-search-input, #gb-flyout-menu-search-input").focus(function() {
                if($(this).val() == "Search Moore") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search Moore");
                }
            });

            // FORM SUMBIT
            $("#gb-search-form").submit(function(e) {
            	e.preventDefault();
            	if($("#gb-search-input").val() != "") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                }
            });

            $(document).on("click keydown", "#gb-flyout-menu-search-button", function(e) {            
                if(_this.AllyClick(e)) {
					if($("#gb-flyout-menu-search-input").val() != "" && $("#gb-flyout-menu-search-input").val() != "Search Moore") {
                		window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                	}
                }
            });

            //Show/hide search
            $(document).on("click keydown", "#gb-search-menu-button", function(e) {            
                if(_this.AllyClick(e)) {
                	e.preventDefault();
                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr("aria-expanded", "true");
                        $("#gb-search-form").attr("aria-hidden", "false");
                        $("#gb-search-form-outer").show("slide", { direction: "right" }, 250);

                        setTimeout(function() {
                            $("#gb-search-input").focus();
                        }, 260);
                    } else {
                    	$(this).attr("aria-expanded", "false");
                        $("#gb-search-form").attr("aria-hidden", "true")
                        $("#gb-search-form-outer").hide("slide", { direction: "right" }, 250);
                    }
                }
            });

            $(document).on("keydown", "#gb-search-input", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                        e.preventDefault();
                        $("#gb-search-menu-button").click().focus();
                    break;
                }
            });
        },

        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },

        "CutHex": function(hex){
        	return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
            	event.preventDefault();
                return true;
            } else if(event.type == "keypress" || event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
            	event.preventDefault();
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

