        window.resizeMiniCalendar20200422113704260 = function () {
        var calId = 'miniCal20200422113704260';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200422113704260';
        var calMonthFullId = 'miniCalMonthFull20200422113704260';
        var calMonthShortId = 'miniCalMonthShort20200422113704260';
        var calGoToCalFullId = 'miniCalGotToCalFull20200422113704260';
        var calGoToCalShortId = 'miniCalGoToCalShort20200422113704260';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

