body.script:
(function () {
  /* Mobile-friendly background image */
  var width = window.innerWidth || document.documentElement.clientWidth,
      el = document.querySelector('.default-background-page-thumbnail'),
      format = '?format=';

  if (el) {
    if (width <= 100) { format += '100w'; }
    else if (width <= 300)  { format += '300w'; }
    else if (width <= 500)  { format += '500w'; }
    else if (width <= 700)  { format += '700w'; }
    else if (width <= 1000) { format += '1000w'; }
    else if (width <= 1500) { format += '1500w'; }
    else { format += 'original'; }

    var url = '' || '/covid19-resources';
    el.style.backgroundImage = 'url("' + url + format + '")';
  }

  function refreshSiteTitlePosition () {
    var banner = document.querySelector( '.site-title-position-bottom #bannerImage' );
    var logo = document.getElementById( 'logo' );
    var bannerImage = document.querySelector( '#bannerImage img' );

    if ( banner && logo ) {
      banner.style.marginBottom = logo.offsetHeight / 2 + 'px';

      if ( bannerImage ) {
        bannerImage.addEventListener('load', function () {
          banner.style.marginBottom = logo.offsetHeight / 2 + 'px';
        } );
      }
    }
  }

  refreshSiteTitlePosition();

  if (Y.Global) {
    Y.Global.on('tweak:change', refreshSiteTitlePosition);
  }
})();
