    window.location = '/cms/One.aspx?portalId=343110&ref=mesTP9fg96fVsgzpWYeogHyNWElcKryjhXIESAn2nmE%3d&pageId=3245482&objectId.116819=13227597&contextId.116819=3245483&parentId.116819=13227597&action.116819=add&objectType.116819=workspace_event&currentDate='+currentDate.format("yyyy-MM-dd")+'&view='+sender.get_viewModeName();
}

function ViewEventClick(sender, theEvent){   
   var url = '/cms/One.aspx?portalId=343110&pageId=3245482&contextId.116819=3245483&parentId.116819=13227597&returnTo=13227597&objectId.116819=' + theEvent.EventId + '&currentDate='+sender.get_currentDate().format("yyyy-MM-dd")+'&view='+sender.get_viewModeName() + (theEvent.Reference == '' ? '' : '&ref=' + theEvent.Reference);   
   window.location = url;
}

