	$(function(){
		$(window).bind("resize", function(){ pgScreenSize(); calendarListView(); });

		var globalIconData = [
			['Learning Resources', 'https://sites.google.com/msd549c.org/msd549canywherelearningplan/home'],
			['Messages to Families', 'https://docs.google.com/document/d/1zYZ6nAfLuFqQA9Ibwz2SWVMitCMaYZfynp9xy3w1sw0/edit?usp=sharing'],
			['Student Support', 'https://www.medford.k12.or.us/domain/2219'],
			['Meal Sites & Times', 'https://www.medford.k12.or.us/Page/5973'],
			['', '#']
		];
		var iconNum = (('4' == "5;4;3;2;1;0") ? 0 : '4');
		var globalLinksHeader = "";
		var globalLinks = "";

		for(var i = 0; i < iconNum; i++) {
			var linkCheck = (globalIconData[i][1] == "[SITECALENDARLINK]") ? "" : globalIconData[i][1];
			globalLinks += "1, " + globalIconData[i][0] + ", " + linkCheck + ", _self;";
		}

		if(iconNum > 0) {
			globalLinksHeader = "Global Links";
		}

		// RESPONSIVE MAIN MENU
		$("body").csRsMenu({
		        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "menuButtonParent"      : "#mobile-menu-bar",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : " MENU",
		        "showSchools"           : "yes",
		        "showTranslate"         : "yes",
		        "translateId"           : "none",
		        "showAccount"           : "yes",
		        "extraMenuOptions"      : globalLinksHeader, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
		        "extraMenuItems"        : globalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
		        "siteID"                : "2209"
		});

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
		        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "menuButtonParent"      : "#sp-content",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "PAGES",
		        "siteID"                : "2209"
		});


		pgScreenSize();
		useChannelForSection();
		dynamicContent();
		customDropdowns('up', 'down');	
		globalIcons();
		modEvents();
		checkSiteDomain(); //will run logo animation if on district
		addFramesetTranslate();

        setupTabs();
	});	


	function setupTabs(){
        var element = $("#sw-content-container3.ui-hp");


        // CREATE TAB CONTAINER
        $(element).prepend("

");

        // CREATE TABS AND SET ATTRIBUTES
        $("div.ui-widget.app", element).not($("div.ui-widget.app div.ui-widget.app", element)).each(function(i) {
            var myHeader = $("div.ui-widget-header h1", this).html();
            $(this).parents("div.region.ui-hp").find("div.tabs-container div.clear").before("
" + myHeader + "");
            if($(this).hasClass("blog")) {
                    $(this).parent().wrap('
'); 
            }
            $('.ui-widget-header h1', this).append('
');

            $(this).appendTo($('#tabbed-apps-cont'));
        });

        $('#tabbed-apps-cont > div').hide();

		$("#sw-content-container3.ui-hp div.tab-button").click(function () {
        	  $(this).addClass('active');
              $('#tabbed-apps-cont > div:eq(' + $(this).index() + ')').fadeIn(300, function(e){ $(this).addClass('active-app-cont') });
              $('#tabbed-apps-cont').fadeIn(300);
       	});

        $('.tabbed-apps-cont-close-trigger').click(function(e){
        	  $("#sw-content-container3.ui-hp div.tab-button.active").removeClass('active');
              $('#tabbed-apps-cont').stop(true, true).fadeOut(300).removeClass('block-ui');
              $('#tabbed-apps-cont .active-app-cont').stop(true, true).fadeOut(300, function(e){ $(this).removeClass('active-app-cont') });
        });
	}

	function pgScreenSize(){
		var getWidth = $(window).width();
		if( getWidth >= 1024 ){
			if( $('#gb-channel-list > #gb-search').length < 1 ){
				$('#gb-search').appendTo('#gb-channel-list');
			}
		}else{
			if( getWidth <= 480){
				appView(['#hp-content']);
			}
			if( $('#mobile-menu-bar > #gb-search').length < 1 ){
				$('#gb-search').appendTo('#mobile-menu-bar');
			}
		}
		multimediaRotator();

		if( $('#magic-white-box').length > 0 ){
			var topPos = $('#gb-logo').offset().top + $('#gb-logo').outerHeight();
			$('#magic-white-box').css({
				top: topPos,
				left: $('#gb-logo').offset().left,
				width: $('#gb-logo').outerWidth()
			});
		}
	}

	function multimediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : 727,
				"imageHeight"             : 321,
				"appHeader"               : "no",
				"titles"                  : "yes",
				"captions"                : "yes",
				"galleryOverlay"	  : "no",
				"linkedElement"		  : ["image"], // ["image", "title", "overlay", "all"]
				"playPause"               : "no",
				"mainControls"            : "yes",
				"contMoreLink"            : "no",
				"contMoreLinkText"        : "Read More",
				"descMoreLink"            : "yes",
				"descMoreLinkText"        : "More ",
				"bullets"                 : "no",
				"bulletControls"          : "no",
		 		"thumbnails"              : "no",
				"thumbnailWidth"          : 122,
				"thumbnailHeight"         : 53,
				"thumbnailSlideDirection" : "horizontal", // horizontal or vertical
				"hoverPause"              : "yes",
		                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
				"fullScreenVideo"         : "no",
				"transitionType"          : "fade", // fade or slide
				"transitionSpeed"         : 3,
				"transitionDelay"         : 8,
				"onImageLoad"             : function() { },
				"allImagesLoaded"         : function() { },
				"onThumbnailLoad"         : function() { },
				"allThumbnailsLoaded"     : function() { },
				"onTransitionStart"       : function() { },
				"onTransitionEnd"         : function() { },
				"watchVidClick"           : function() { },
				"closeVidClick"           : function() { },
				"allLoaded"               : function() {
					if( $("#hp-slideshow .rs-gallery-control").length > 0 ){
						$("#hp-slideshow .rs-gallery-control.back").addClass('icon-font icon-sm-arrow left');
						$("#hp-slideshow .rs-gallery-control.next").addClass('icon-font icon-sm-arrow right');
					}
				}
			});
		}
	}

	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "no",
	            dayWeek     : "no"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });
	}

	function appView(toChange){
	    $.each(toChange, function(k, v){
	        $("div.ui-widget.app", v).each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else{
				if( $('.ui-widget-header h1 span.accordion-handle', this).length <= 0){
					$('.ui-widget-header h1', this).append('+');
				}
			}
		});

		//DOC accordion header click
		$("div.ui-widget-header", v).off('click').click(function(){
			var clickedApp = $(this).parent().parent().attr("id");
			if($(this).parent().hasClass('active')){ //toggles already open to close
				$(this).parent().removeClass('active');
				$("span.icon-font", this).removeClass('icon-arrow-down').addClass('icon-arrow-left');
			}else{ //opens an already closed one
				$("div.ui-widget.app.active .ui-widget-header h1 span.icon-font", v).removeClass('icon-arrow-down').addClass('icon-arrow-left');
				$("div.ui-widget.app.active", v).removeClass("active");
				$(this).parent().addClass("active");
				$("span.icon-font", this).removeClass('icon-arrow-left').addClass('icon-arrow-down');
			}
			smoothScroll("#" + clickedApp, 10);
		});
	    });
	}	
	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		if($(".sw-mystart-button.signin").length > 0){
			$('#custom-mystart-signin').append($(".sw-mystart-button.signin").html());
