	function checkSiteDomain(){
		if('medfordanywherelearning' == districtAlias){ //on the district
			$('#gb-page').addClass('district');
			$(window).load(animateLogo);
		}else{ //on a subsite
			$('#gb-page').addClass('subsite')
				.append('
');
			$(window).load(function(){
				var topPos = $('#gb-logo').offset().top + $('#gb-logo').outerHeight();
				$('#magic-white-box').css({
					top: topPos,
					left: $('#gb-logo').offset().left,
					width: $('#gb-logo').outerWidth()
				});
			});

			var siteName = "Medford Anywhere Learning COVID-19".split(" ");

			$('#subsite-sitename').append('
' + siteName.slice(0, siteName.length - 1).join(" ") + '');
			$('#subsite-sitename').append('
' + siteName[siteName.length - 1] + '');
			$('#gb-custom-mystart .logo-icon > img').attr('src', '/cms/lib/OR01915738/Centricity/Template/23/images/m-logo-grey.png');

			if( $('#gb-logo #subsite-logo').attr('src').indexOf('default-man') >= 0 || $('#gb-logo #subsite-logo').attr('src') == "" ){
				$('#magic-white-box').hide();
				$('#gb-logo #subsite-logo').hide();
				$('#gb-logo').width('107px');
			}
		}
	}

	function animateLogo(img){
		var stage1Timer = 400;
		var stage2Timer = 400;
		var stage3Timer = 700;
		var stage4Timer = 700;
		$('#gb-logo > a > .hidden').show(0);
		$('#gb-logo > a').css('height', $('#gb-logo > a').outerHeight());
		$('#gb-logo > a > .hidden').hide(0);

		setTimeout(function(){ //show stage1
			$('#gb-logo > a #logo-stage-1').fadeIn(stage1Timer/2);
			setTimeout(function(){ //show stage2
				$('#gb-logo > a #logo-stage-2').fadeIn(stage2Timer/2);
				setTimeout(function(){ //show stage3
					$('#gb-logo > a #logo-stage-3').fadeIn(900, 'easeInQuart');
					setTimeout(function(){ //show stage4
						$('#gb-logo > a #logo-stage-4').fadeIn(900, 'easeInQuart');
						$('#gb-logo > a').css('height', 'auto');
					}, stage4Timer);
				}, stage3Timer);
			}, stage2Timer);
		}, stage1Timer);
	}

