        $(document).ready(function () {
            //"/<[^\/>][^>]*><\/[^>]+>/"
            setTimeout(function () {
                //$('div:contains(" Column ")').remove();                
                //$('*:empty').remove();
                $('div.container').find('div.ms-rtestate-field').each(function (index) {

                    if ($(this).prev('div') != undefined && $(this).prev('div')[0] != undefined && $(this).prev('div')[0].innerText.indexOf('Column') > -1) {
                        $(this).prev('div')[0].remove();
                    }
                    if ($(this).closest('div.row') != undefined && $(this).closest('div.row')[0] != undefined && $(this).closest('div.row')[0].innerText === '') {
                        $(this).closest('div.row')[0].remove();
                    }
                });

                $('div.container').find('div.row').each(function (index) {
                    if (this.innerText === '') {
                        this.remove();
                    }
                });
            }, 5000);
        });

