	function migrationMods(){
		//DOC .ui-breadcrumb-first -- REPLACE TEXT OF .ui-breadcrumb-first
		$("li.ui-breadcrumb-first > a > span").text("Home");
		$("a.more-link span").text("More »");
	}

	function loadFlash(){
		// #flash-bottom-curve -- IF #flash-bottom-curve EXISTS THEN ADD THE SWFObject
		if ($("#flash-bottom-curve").size())
		{
			var so = new SWFObject("/cms/lib/OR01001504/Centricity/Template/2/X01-101A-bottomCurve.swf","mymovie", "480", "170", "9", "#FFFFFF");
			so.addParam("wmode", "transparent");
			so.addParam("scale", "noscale");
			so.addVariable("topCurveColor", topCurveColor);
			so.addVariable("rightColColor", rightColColor);
			so.write("flash-bottom-curve");

		}
		// #sp-header -- IF #sp-header EXISTS THEN ADD THE SWFObject
		if($("#sp-header").size())
	         {
	            	var so = new SWFObject("/cms/lib/OR01001504/Centricity/Template/2/X01-101A-sp-flash.swf","mymovie", "960", "120", "9", "#FFFFFF");
			so.addParam("wmode", "transparent");
			so.addVariable("schoolAbbr", "myrtlepointschools");
			if(window.location.host == "translate.googleusercontent.com"){
				var locationText = String(window.location);
				locationText = locationText.split("//");
				locationText = locationText[2].split("/");
				so.addVariable("homeURL", locationText[0]);	
			} else {
				so.addVariable("homeURL", window.location.host);
			}
			so.addVariable("folderURL", "/cms/lib/OR01001504/Centricity/Template/2");
			so.addVariable("topBGColor", topBGColor);
			so.addVariable("topCurveColor", topCurveColor);
			so.addVariable("bottomLineColor", bottomLineColor);
			so.addVariable("bottomLine2Color", bottomLine2Color);
			so.addVariable("schoolNameColor", schoolNameColor);
			so.addVariable("tagLineColor", tagLineColor);
			if(schoolName == "default"){
			so.addVariable("schoolName", "Myrtle Point School District");
			}else{
			so.addVariable("schoolName", schoolName);
			}
			so.addVariable("tagLine", tagLine);
			so.addVariable("subpageSchoolNameSpacing", subpageSchoolNameSpacing);
			so.addVariable("subpageSchoolLeftSpacing", subpageSchoolLeftSpacing);
			so.addVariable("siteLevelPhotos", siteLevelPhotos);
			so.addVariable("schoolLogos", schoolLogos);
			so.addVariable("iconSwitch", iconSwitch);
			so.addVariable("globalIcons", globalIcons);
			so.addVariable("icon1Link", icon1Link);
			so.addVariable("icon2Link", icon2Link);
			so.addVariable("icon3Link", icon3Link);
			so.addVariable("icon4Link", icon4Link);
			so.addVariable("icon1Target", icon1Target);
			so.addVariable("icon2Target", icon2Target);
			so.addVariable("icon3Target", icon3Target);
			so.addVariable("icon4Target", icon4Target);
			so.write("sp-header");

	        }
	        // #hp-header -- IF #hp-header EXISTS THEN ADD THE SWFObject
	        if($("#hp-header").size())
	        {
		        var so = new SWFObject("/cms/lib/OR01001504/Centricity/Template/2/X01-101A-hp-flash.swf","mymovie", "960", "282", "9", "#FFFFFF");
			so.addParam("wmode", "transparent");
			so.addVariable("schoolAbbr", "myrtlepointschools");
			so.addVariable("SiteID", "1");
	            	if(window.location.host == "translate.googleusercontent.com"){
				var locationText = String(window.location);
				locationText = locationText.split("//");
				locationText = locationText[2].split("/");
				so.addVariable("homeURL", locationText[0]);	
			} else {
				so.addVariable("homeURL", window.location.host);
			}
			so.addVariable("folderURL", "/cms/lib/OR01001504/Centricity/Template/2/");
			so.addVariable("topBGColor", topBGColor);
			so.addVariable("topCurveColor", topCurveColor);
			so.addVariable("bottomLineColor", bottomLineColor);
			so.addVariable("bottomLine2Color", bottomLine2Color);
			so.addVariable("schoolNameColor", schoolNameColor);
			so.addVariable("tagLineColor", tagLineColor);
			if(schoolName == "default"){
				so.addVariable("schoolName", "Myrtle Point School District");
			}else{
				so.addVariable("schoolName", schoolName);
			}
			so.addVariable("tagLine", tagLine);
			so.addVariable("schoolNameSpacing", schoolNameSpacing);
			so.addVariable("schoolLogos", schoolLogos);
			so.addVariable("siteLevelPhotos", siteLevelPhotos);
			so.addVariable("staticImage", "no");
			so.addVariable("iconSwitch", iconSwitch);
			so.addVariable("globalIcons", globalIcons);
			so.addVariable("numOfIcons", "0");
			so.addVariable("icon1Link", icon1Link);
			so.addVariable("icon2Link", icon2Link);
			so.addVariable("icon3Link", icon3Link);
			so.addVariable("icon4Link", icon4Link);
			so.addVariable("icon1Target", icon1Target);
			so.addVariable("icon2Target", icon2Target);
			so.addVariable("icon3Target", icon3Target);
			so.addVariable("icon4Target", icon4Target);
			so.write("hp-header");
	}


        }
        function resizeh(h){

		$("#mymovie1").height(h);
		$('#SectionName').height(h);

	}
        function subpageSectionName(){
        	// #SectionName -- IF #SectionName EXISTS THEN ADD THE SWFObject
		if($("#SectionName").size())
		{
			var so = new SWFObject("/cms/lib/OR01001504/Centricity/Template/2/X01-101A-sectionName.swf","mymovie1", "210", "40", "9", "#FFFFFF");
			so.addParam("wmode", "transparent");
			so.addParam("scale", "noscale");
			so.addParam("allowScriptAccess", "always");
			so.addVariable("padding", "10");
			so.addVariable("districtAbbr", "myrtlepointschools");
			if(window.location.host == "translate.googleusercontent.com"){
				var locationText = String(window.location);
				locationText = locationText.split("//");
				locationText = locationText[2].split("/");
				so.addVariable("homeURL", locationText[0]);	
			} else {
				so.addVariable("homeURL", window.location.host);
			}
			so.addVariable("folderURL", "/cms/lib/OR01001504/Centricity/Template/2/");
			so.addVariable("sectionName", "");
			so.addVariable("sectionColor", sectionColor);
			so.write("SectionName");


		}
	}

////// PREDESIGN FUNCTIONS ///////
	function customMyStart(){
		// ADDS ON CUSTOM MYSTART STYLE
	        if(customMystart == "on"){
	            var customStyleSheet='';
	            $("body").append(customStyleSheet);
	            $("#sw-search-button img[alt*='Search']").attr("src","/cms/lib/OR01001504/Centricity/Template/2/sw-mystart-search-custom.png");
	        }
	        $("#sw-mystart-right").append('
Grades
E-Mail');
	        $("#sw-mystart-search").before($(".sw-mystart-button.email"));
	        $(".sw-mystart-button.email").before($(".sw-mystart-button.grades"));
	    }
	function footerSwitch(){
