	function changeAllColors(){
		 // CHANGES THE COLORS
		 $("body").css("background-color", bodyColor);
		 $("ul.ui-breadcrumbs a").css("color", topBGColor);
	         $("#gb-nav").css("background-color", navColor);
	         $("#gb-footer-inside").css("background-color", footerColor);
	         $("#gb-footer-inside").css("color", footerTextColor);
	         $("li.sw-channel-item > a").css("color", channelUnselected);
	         $("li.sw-channel-item.active > a span").css("color", channelSelected);
	         $(".sp.column.one div.ui-widget.app .ui-widget-header, .sp.column.one a").css("color", sectionColor);
	         $(".hp.column.one").css("background-color", leftColColor);
	         $(".sp.column.one").css("background-color", leftColColor);
	         $("#hp-content-container").css("background-color", topCurveColor);
	         $("div.hp.column.three").css("background-color", rightColColor);
	         $("li.sw-channel-item > a").hover(function()
	         {
	             $(this).css("color", channelSelected);
	         },
	         function()
	         {
	             $(this).css("color",channelUnselected);
	         });
	         $("div.navigation a").css("color", shortcutUnselected);
	         $("div.navigation li.active a").css("color", shortcutSelected);
	         $("div.navigation a").hover(
			function () {
	    			$(this).css("color", shortcutHover);
	  		}, 
	  		function () {
	    			$(this).css("color", shortcutUnselected);
	    			$("div.navigation li.active a").css("color", shortcutSelected);
  		});
	}
	function columnOneHeader(){
		//DOC .hp.column.one div.ui-widget.app -- REPLACE .ui-widget.apps WITH SWFObject IN .hp.column.one
		$(".hp.column.one div.ui-widget.app").each( function(i){
	            	var myID = "app-" + $(this).parent().parent().attr("id").replace("module-content-", "");
	                var headerText = $("div.ui-widget-header h1",this).text();
	                $("div.ui-widget-header",this).attr("id", myID);
	                var so = new SWFObject("/cms/lib/OR01001504/Centricity/Template/2/X01-101A-ABtitle.swf","mymovie", "210", "40", "9", "#FFFFFF");
	                so.addParam("wmode", "transparent");
	                so.addVariable("districtAbbr", "myrtlepointschools");
	                if(window.location.host == "translate.googleusercontent.com"){
				var locationText = String(window.location);
				locationText = locationText.split("//");
				locationText = locationText[2].split("/");
				so.addVariable("homeURL", locationText[0]);	
			} else {
				so.addVariable("homeURL", window.location.host);
			}
	                so.addVariable("folderURL", "/cms/lib/CA01001264/Centricity/Template/2/");
	                so.addVariable("ABtitle", headerText); 
	                so.addVariable("ABtitleColor", AB1titleColor);
	                so.write(myID);
		});
	}
