	function SWEndPreviewMode() { 
		var data = "{}";
		var success = "window.location='';";
		var failure = "CallControllerFailure(result[0].errormessage);";
		CallController("https://www.mpsd.k12.or.us/site/SiteController.aspx/EndPreviewMode", data, success, failure);
	}
	
