 			function addCommas(x) {
			    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
			}

    		makeMap();


			function makeMap() {
				var width = $(".map").width(),
				    height = width*1.25;

				var projection = d3.geoMercator()
				    .center([-120.475, 45.93])
				    .scale(width*6.8)
                    .translate([(width/2)+3, (height/2)-22]);

				var path = d3.geoPath()
				    .projection(projection);

                var radius = d3.scaleSqrt()
                    .domain([0, 2000])
                    .range([0, 55]);

				var svg = d3.select(".map").append("svg")
				    .attr("width", width)
				    .attr("height", height);



                d3.queue()
                    .defer(d3.json,"data/orwa-topo.json")
                    .defer(d3.csv,"data/orwa2.csv")
                    .await(ready)

                function ready(error, oregon, data) {

                  if (error) return console.error(error);


                    var countydata = [];
                    data.forEach(function(d) {
                        countydata[d.geoid] = d;
                    });                


                  var counties = oregon.objects.counties.geometries;
                  counties.forEach(function(d) {
                      d.properties = countydata[d.id];
                  })                 



                  svg.append("g")
                      .attr("class", "land")
                    .selectAll("path")
                       .data(topojson.feature(oregon, oregon.objects.counties).features)
                    .enter().append("path")
                      .attr("d", path)
                      .attr("class",function(d) {
                          return "county fips_"+d.properties.id;
                      })
					   .on("mouseout", function(d) {
    					       					   d3.select("circle.fips_"+d.properties.id).classed("active",false);

						   d3.select(".tip").classed("hideit", true);
						    d3.select(this.parentNode.appendChild(this))
						        .classed("hover",false);
					   })
					   .on("mouseover", function(d) {
    					   d3.select("circle.fips_"+d.properties.id).classed("active",true);
						   d3.select(".tip")
						        .classed("hideit", false);		
						    d3.select(this.parentNode.appendChild(this))
						        .classed("hover",true);	
					   })


					   .on("mousemove", function(d) {
    					   var rightlimit = width-200;
    					   var mousep = d3.mouse(this);
							var xPosition = mousep[0];
							var yPosition = mousep[1];
							var yPos = yPosition-20;
							var xPos = xPosition-100;
							if (xPos<0) { 
    							xPos = 0; 
    				        } else if (xPos>rightlimit) {
        				        xPos = rightlimit;
    				        }
							var confirmed = d.properties.confirmed;
							var deaths = d.properties.deaths;
                        	var s = "s";
                        	var c = "s";
                        	var deathline = "";
                        	var countyname = d.properties.name;
                        	countyname = countyname.replace(/, (Oregon|Washington)/i,"");
                        	if (deaths>0) {
                            	if (deaths==1) {
                                	s = "";
                            	}
                            	deathline = "
"+addCommas(deaths)+" death"+s+"";
                        	}
							if (confirmed==1) {
    							c = "";
							}

                            var text = "
"+countyname+""
                                + "
"+addCommas(confirmed)+" confirmed case"+c+""
                                + deathline;


							d3.select(".tip")
								.style("left", xPos + "px")
								.style("top", yPos + "px")				
					   		d3.select(".tip")
					   		  .html(text);
					   });

                  var city = svg.append("g");
                  d3.csv("data/cities.csv",function(error,cities) {
                      city.selectAll("circle")
                        .data(cities)
                        .enter()
                      .append("circle")
                        .attr("class","citybubble")
                        .attr("cx", function(d) {
                            return projection([d.lon,d.lat])[0];
                        })
                        .attr("cy", function(d) {
                            return projection([d.lon,d.lat])[1];
                        })
                        .attr("r",3);

                      city.selectAll("text")
                        .data(cities)
                        .enter()
                       .append("text")
                        .attr("x", function(d) {
                            return projection([d.lon,d.lat])[0];
                        })
                        .attr("y", function(d) {
                            return projection([d.lon,d.lat])[1];
                        })
                        .attr("dy",-6)
                        .attr("dx",function(d) {
                            if ((d.city=='Spokane')||(d.city=='Medford')) {
                                return -2;
                            } else {
                                return 2;
                            }
                        })
                        .attr("class","citylabel")
                        .attr("text-anchor",function(d) {
                            if ((d.city=='Spokane')||(d.city=='Medford')) {
                                return "end";
                            } else {
                                return "start";
                            }
                        })
                        .text(function(d) {return d.city;});


                    });


                  svg.append("g")
                      .attr("class", "bubble")
                    .selectAll("circle")
                      .data(topojson.feature(oregon, oregon.objects.counties).features
                        .sort(function(a, b) { return b.properties.confirmed - a.properties.confirmed; }))
                    .enter().append("circle")
                      .attr("class",function(d) {
                          return "fips_"+d.properties.id;
                      })
                      .attr("pointer-events","none")
                      .attr("transform", function(d) { return "translate(" + path.centroid(d) + ")"; })
                      .attr("r", function(d) { return radius(d.properties.confirmed); });


                    var legend = svg.append("g")
                        .attr("class", "legend")
                        .attr("transform", "translate("+(width-(radius(1e3)))+","+(height)+")")
                      .selectAll("g")
                        .data([1e2, 5e2, 1e3])
                      .enter().append("g");

                    legend.append("circle")
                        .attr("cy", function(d) { return -radius(d); })
                        .attr("r", radius);

                    legend.append("text")
                        .attr("y", function(d) { return -2 * radius(d); })
                        .attr("dy", "1.3em")
                        .text(d3.format(".1s"));


				}
			}



            function stateTests() {

        		var divwidth = 	parseInt(d3.select("#staterank").style('width'),10);
        		var divheight = divwidth*.4;
                if (divheight>300) {
                    divheight = 300;
                }
                // create the svg
        		var svg = d3.select("#staterank").append("svg"),
                    margin = {top: 10, right: 5, bottom: 50, left: 30},
        		    width = divwidth - margin.left - margin.right,
        		    height = divheight - margin.top - margin.bottom,
                    g = svg.append("g").attr("transform", "translate(" + margin.left + "," + margin.top + ")");

                // set x scale
                var x = d3.scaleBand()
                    .rangeRound([0, width])
                    .paddingInner(0.06)
                    .align(0.1);

                // set y scale
                var y = d3.scaleLinear()
                    .rangeRound([height, 0]);

                // set the colors
                var z = d3.scaleOrdinal()
                    .range(["#d73027","#386888"]);

                // load the csv and create the chart
                d3.csv("data/state_testing.csv", function(d, i, columns) {

                  for (i = 1, t = 0; i < 3; ++i) {
                       t += d[columns[i]] = +d[columns[i]];
                  }
                  d.total = t;
                  return d;
                }, function(error, data) {
                  if (error) throw error;
                  var keys = data.columns.slice(1,3);
                  data.sort(function(a, b) { return a.total - b.total; });
                  x.domain(data.map(function(d) { return d.state; }));
                  y.domain([0, d3.max(data, function(d) { return d.total; })]).nice();
                  z.domain(keys);



                  var r = d3.values(data).filter(function(q){ return q.state == 'OR'; });
                  var o = r[0];
                  var orbar_x = x(o.state)+margin.left;
                  var orbar_y = y(o.total);
                  var orleft = orbar_x-(x.bandwidth()*8);
                  var orright = orbar_x+(x.bandwidth()*8);

                  g.append("g")
                    .selectAll("g")
                    .data(d3.stack().keys(keys)(data))
                    .enter().append("g")
                      .attr("fill", function(d) { return z(d.key); })
                    .selectAll("rect")
                    .data(function(d) { return d; })
                    .enter().append("rect")
                      .attr("x", function(d) { return x(d.data.state); })
                      .attr("y", function(d) { return y(d[1]); })
                      .attr("class", function(d) { return d.data.state; })
                      .attr("height", function(d) { return y(d[0]) - y(d[1]); })
                      .attr("width", x.bandwidth())
                    .on("mouseover", function(d) { 
                        var tipwide = 45;
                        var tiphigh = $(".statetip").height()+10;
                        var rightlimit = divwidth-(x.bandwidth()*5)-tipwide;
                        var state = d.data.state;
                        var total = d.data.total;
                        var pos = d.data.positive;
                        var neg = d.data.negative;
                        var tests = d.data.tests;
                        var pos_pct = (pos/total)*100;
                        var neg_pct = (neg/total)*100;
                        var tool_x = x(state)+(x.bandwidth()/2)+margin.left;
                        var tool_y = y(total)+margin.top-5;
                        var tip_y = tool_y-100;
                        var tip_x = tool_x-(tipwide);
                        if (tip_x<35) { 
                            tip_x = 35; 
        /*
                        } else if (tip_x > rightlimit) {
                            tip_x = rightlimit;
        */
                        }
                        d3.select(".statetip")
            			  	.classed("hidden",false)
            			  	.style("top",tip_y+"px")
            			  	.style("left",tip_x+"px");
                        d3.select(".statetip .statename").text(states[state]);
                        d3.select(".statetip .all_tests").text(addCommas(tests)+" tests");

                        d3.select(".statetip .test_num").text(total.toFixed()+" per 100k");
                        d3.select(".statetip .pos_num").text(pos_pct.toFixed()+"%");
                        d3.select(".statetip .neg_num").text(neg_pct.toFixed()+"%");
                        if (state!='OR') {
                            $("rect."+state).css("fill-opacity",.8);
                        }
                    })
                    .on("mouseout", function(d) { 
                        var state = d.data.state;
                        d3.select(".statetip")
            			  	.classed("hidden",true)
                        if (state!='OR') {
                            $("rect."+state).css("fill-opacity",.5);
                        }
                    })


                  g.append("g")
                      .attr("class", "x axis")
                      .attr("transform", "translate(0," + height + ")")
                      .call(d3.axisBottom(x));

                  g.append("g")
                      .attr("class", "y axis")
                      .call(d3.axisLeft(y).ticks(null))
                    .append("text")
                      .attr("x", 2)
                      .attr("y", y(y.ticks().pop()) + 0.5)
                      .attr("dy", "0.32em")
                      .attr("fill", "#000")
                      .attr("font-weight", "bold")
                      .attr("text-anchor", "start");


                	svg.append("text")
                        .attr('x',orbar_x+(x.bandwidth()/2))
                        .attr('y',height/2)
                        .attr('dy',"-5px")
                        .attr('class','grafik_text')
                        .attr("text-anchor","middle")
                        .text("Oregon");

        		    svg.append('svg:line')
        		        .attr('class','best_fit_line')
        		        .attr('y1',orbar_y+margin.top)
        		        .attr('y2',height/2+3)
        		        .attr('x1',orbar_x+(x.bandwidth()/2))
        		        .attr('x2',orbar_x+(x.bandwidth()/2));


                  var legend = g.append("g")
                      .attr("font-family", "sans-serif")
                      .attr("font-size", 10)
                      .attr("text-anchor", "start")
                    .selectAll("g")
                    .data(keys.slice().reverse())
                    .enter().append("g")
                      .attr("transform", function(d, i) { return "translate(" +  i*70+ ",0)"; });

                  legend.append("rect")
                      .attr("x", 12)
                      .attr("width", 12)
                      .attr("height", 12)
                      .attr("fill", z)
                      .attr("class","legendbox");

                  legend.append("text")
                      .attr("x", 30)
                      .attr("y", 6)
                      .attr("dy", "0.22em")
                      .text(function(d) { return d; });

                });

            }

