                        var countydata = [];
                        allcounties.forEach(function(d) {
                            allcases += d.attributes.Confirmed;
                            alldeaths += d.attributes.Deaths;
                            countydata[d.attributes.FIPS] = d.attributes;
                        });                

                        $(".toll .cases").text(addCommas(allcases));
                        $(".toll .deaths").text(addCommas(alldeaths));
                      var counties = us.objects.counties.geometries;
                      counties.forEach(function(d) {
                          d.properties = countydata[d.id];
                      })


                    var legend = svg.append("g")
                        .attr("class", "legend")
                        .attr("transform", "translate("+(width-(radius(3e4)))+","+height+")")
                      .selectAll("g")
                        .data([1e3, 1e4, 3e4])
                      .enter().append("g");

                    legend.append("circle")
                        .attr("cy", function(d) { return -radius(d); })
                        .attr("r", radius);

                    legend.append("text")
                        .attr("y", function(d) { return -2 * radius(d); })
                        .attr("dy", "1.3em")
                        .text(d3.format(".1s"));


                      svg.append("g")
                          .attr("class", "land")
                        .selectAll("path")
                           .data(topojson.feature(us, us.objects.states).features)
                        .enter().append("path")
                          .attr("d", path);




                        var city = svg.append("g")
                             .attr("class","cities")                        
                        d3.json("data/cities.json",function(error,cities) {
                          city.selectAll("path")
                            .data(cities)
                            .enter()
                          .append("path")
                            .attr("d",path);
                        })


                       svg.append("g")
                          .attr("class", "bubble")
                        .selectAll("circle")
                          .data(topojson.feature(us, us.objects.counties).features
                            .sort(function(a, b) { return b.properties.Confirmed - a.properties.Confirmed; }))
                        .enter().append("circle")
                          .attr("pointer-events","none")
                          .attr("transform", function(d) { return "translate(" + path.centroid(d) + ")"; })
                          .attr("r", function(d) { if (d.properties.Confirmed) { return radius(d.properties.Confirmed);} });


				}
			});
