	// Progressive enhancement if JS is available.
	// Function fires on page load to hide all branches of the nav tree
	// except the branch that the current page is under.
	jQuery(document).ready(function($) {
  		$("nav#secondaryNav li.page_item_has_children:not(.current_page_ancestor)").children().each(function() {
    		if ( $(this).is("button") ) {
    			$(this).attr({
      				'aria-expanded': false,
        			'aria-label': "Open sub navigation"
      			});
      			$(this).children('span').first().attr('class', 'glyphicon glyphicon-triangle-left');
    		} else if ( $(this).is("ul") ) {
      			$(this).attr('aria-hidden', true);
    		}
  		});
	});

	// Add a click event to the secondary nav buttons to expand/collapse branches of the nav tree
	// by changing the aria-hidden attribute. Also changes the glyphicon direction.
	(function($) {
  		$("#secondaryNav button").click(function() {
    		var $details = $(this).parent();
    		var state = $(this).attr("aria-expanded") === "false" ? true : false
    		$(this).attr("aria-expanded", state);
    		$(this).next().attr("aria-hidden", !state);
    		if (state) {
      			$(this).attr("aria-label", "Close sub navigation");
      			$(this).children('span').first().attr('class', 'glyphicon glyphicon-triangle-bottom');
    		} else {
      			$(this).attr("aria-label", "Open sub navigation");
      			$(this).children('span').first().attr('class', 'glyphicon glyphicon-triangle-left');
    		}
  		});
	})(jQuery);
