            	$("h1.ui-article-title, p.ui-article-description ", this).wrapAll("
");
            });


            $('#hp-app-wrap div:not(.region-b) .region').each(function() {
            	if($(this).is(':empty')) {
            		$(this).parent().remove();
            	}
            });





        },

