        "SocialTabs":function() {
        var _this = this;
        	$("#sw-content-container4.ui-hp").tabBlocks();

            var vendors = ["twitter", "facebook", "youtube", "instagram", "basdtv"];
            $("#sw-content-container4.ui-hp .tabs-container .tab-button").each(function() {
                var thisButton = this;	
                var buttonText = $.trim($(this).text()).toLowerCase();

                $.each(vendors, function(index, vendor) {
                    if(buttonText.indexOf(vendor) > -1) {
                        $(thisButton).addClass(vendor);

                        return false;
                    }
                });
            });

            $("#sw-content-container4.ui-hp .app").each(function() {
            	var network = $(this).find('.ui-widget-header h1').text().toLowerCase();
                $(this).addClass(network);


            });


            //SOCIAL TABS MOBILE
            if(_this.GetBreakPoint !== 'desktop') {
                $('#hp-social-tabs h1').each(function() {
                    var thisButton = this;	
                    var buttonText = $.trim($(this).text()).toLowerCase();

                    $.each(vendors, function(index, vendor) {
                        if(buttonText.indexOf(vendor) > -1) {
                            $(thisButton).addClass(vendor);

                            return false;
                        }
                    });
                });
            }

            //ADD EXTRA SPAN TO TAB BUTTON STRUCTURE
            $('#sw-content-container4 .tab-button').append('')

        },

