        "Search": function() {
            var _this = this;
            // //DON"T ALLOW USER TO SUBMIT AN EMPTY FORM
            $('#sw-search-input').off();
            $('#gb-search-input').on('keydown', function(e){
                if(e.which == 13){//Enter key pressed
                    e.preventDefault();
                     if($.trim($("#gb-search-input").val()) != "I’m Searching for..." && $.trim($("#gb-search-input").val()) != "") {


                        window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                    }

                } 


            });

            $("#sw-search-button").off().removeAttr("onclick").attr("href", "#");
            $("#gb-search-submit").on("click keydown", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();

                    if($.trim($("#gb-search-input").val()) != "I’m Searching for..." && $.trim($("#gb-search-input").val()) != "") {
                        window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                    }
                }


            });


            $('#gb-top-bar button#find').on('click', function() {

            	if($(this).attr("aria-expanded") == "false") {
                	$(this).attr('aria-expanded', true).attr('aria-label', 'Close Search Form');
                    $('#gb-search-form-bg').addClass('showing');
                    $(this).addClass('isOpen');
                    $('#gb-search-input').focus();
                    $('#cs-contrast-bar-outer, #gb-top-bar').addClass('fixed');
                    $('body').css('overflow', 'hidden');




                }else {
                	closeSearch();

                }

            });

            //ESCAPE FROM SEARCH
            $('#gb-search-form-bg').on('keydown', function(e) {
            	if(e.which == 27) {//ESC PRESSED
                	closeSearch();
                	$('button#find').focus();
                }
            });


            //SEARCH TEXT
            $("#gb-search-input").focus(function() {
                if($(this).val() == "I’m Searching for...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("I’m Searching for...");
                }
            });


            //CONTROL FOCUS WHEN PRESSING SHIFT + TAB WHEN SEARCH IS OPEN
            $('#gb-search-submit').on('keydown', function(e) {
            		if(e.shiftKey && e.which === 9) {
                    	e.preventDefault();
						$('#gb-search-input').focus();
                    }
            });

            $('#gb-search-input').on('keydown', function(e) {
            		if(e.shiftKey && e.which === 9) {
                    	e.preventDefault();
						$('#quick-lang li:last-child').find('a').focus();

                        closeSearch();
                    }
            });





            //CLOSE SEARCH
            function closeSearch() {
            	$('#gb-top-bar button#find').attr('aria-expanded', false).attr('aria-label', 'Open Search Form');
                $('#gb-search-form-bg').removeClass('showing');
                $('#gb-top-bar button#find').removeClass('isOpen');
                $('body').css('overflow', 'visible');


            }


        },

