        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },



        "ModEvents":function() {
        	if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "yes"
                });
                this.EventsByDay(".upcomingevents .ui-articles");
            }
        },

        "EventsByDay":function(container) {

		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
		$(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}


			});
             //WRAP DATE AND MONTH IN A CONTAINER
             $(".joel-day, .joel-month", this).wrapAll("");




             //TOGGLE EVENTS IF THERE ARE MORE THAN 3 ON A GIVEN DAY
             $('.upcoming-column.left').addClass('ui-clear');
		});




        $('.upcomingevents').find(".ui-articles li").each(function(){
                  var eventDate = $(this).find(".adam-hug").html();
                  var eventMonth = $(this).find(".joel-month").text();
                  var eventNumberDay = $(this).find(".jeremy-date").text();
                  var eventDay = $(this).find(".joel-day").html();
                  //var eventDetails = $(this).find(".upcoming-column.right").html();
                  var ariaMonth = "";
                  var ariaDate = "";
                  switch(eventMonth){
                    case "jan":
                      ariaMonth = "January";
                    break;
                    case "feb":
                      ariaMonth = "February";
                    break;
                    case "mar":
                      ariaMonth = "March";
                    break;
                    case "apr":
                      ariaMonth = "April";
                    break;
                    case "may":
                      ariaMonth = "May";
                    break;
                    case "jun":
                      ariaMonth = "June";
                    break;
                    case "jul":
                      ariaMonth = "July";
                    break;
                    case "aug":
                      ariaMonth = "August";
                    break;
                    case "sep":
                      ariaMonth = "September";
                    break;
                    case "oct":
                      ariaMonth = "October";
                    break;
                    case "nov":
                      ariaMonth = "November";
                    break;
                    case "dec":
                      ariaMonth = "December";
                    break;
                    default: ariaMonth = eventMonth;
                  }
                  switch(eventNumberDay){
                    case "1":
                      ariaDate = "First";
                    break;
                    case "2":
                      ariaDate = "Second";
                    break;
                    case "3":
                      ariaDate = "Third";
                    break;
                    case "4":
                      ariaDate = "Fourth";
                    break;
                    case "5":
                      ariaDate = "Fifth";
                    break;
                    case "6":
                      ariaDate = "Sixth";
                    break;
                    case "7":
                      ariaDate = "Seventh";
                    break;
                    case "8":
                      ariaDate = "Eighth";
                    break;
                    case "9":
                      ariaDate = "Ninth";
                    break;
                    case "10":
                      ariaDate = "Tenth";
                    break;
                    case "11":
                      ariaDate = "Eleventh";
                    break;
                    case "12":
                      ariaDate = "Twelveth";
                    break;
                    case "13":
                      ariaDate = "Thirteenth";
                    break;
                    case "14":
                      ariaDate = "Fourteenth";
                    break;
                    case "15":
                      ariaDate = "Fifteenth";
                    break;
                    case "16":
                      ariaDate = "Sixteenth";
                    break;
                    case "17":
                      ariaDate = "Seventeenth";
                    break;
                    case "18":
                      ariaDate = "Eighteenth";
                    break;
                    case "19":
                      ariaDate = "Nineteenth";
                    break;
                    case "20":
                      ariaDate = "Twentieth";
                    break;
                    case "21":
                      ariaDate = "Twenty-first";
                    break;
                    case "22":
                      ariaDate = "Twenty-second";
                    break;
                    case "23":
                      ariaDate = "Twenty-third";
                    break;
                    case "24":
                      ariaDate = "Twenty-fourth";
                    break;
                    case "25":
                      ariaDate = "Twenty-fifth";
                    break;
                    case "26":
                      ariaDate = "Twenty-sixth";
                    break;
                    case "27":
                      ariaDate = "Twenty-seventh";
                    break;
                    case "28":
                      ariaDate = "Twenty-eighth";
                    break;
                    case "29":
                      ariaDate = "Twenty-ninth";
                    break;
                    case "30":
                      ariaDate = "Thirtieth";
                    break;
                    case "31":
                      ariaDate = "Thirty-first";
                    break;
                    default: ariaDate = eventNumberDay;
                  }

                  $(this).find('.sw-calendar-block-date').attr('aria-label', ariaMonth+" "+ariaDate);

                })



				//MOVE DATE BEFORE DAY AND MONTH
                $('.jeremy-date').each(function() {
                	var adamHug = $(this).prev();
                    $(this).insertBefore(adamHug);
                });


                //MOVE MONTH BEFORE DAY
                $('.adam-hug').each(function() {
                	var eventMonth = $(this).find('.joel-month');
                    $(eventMonth).prependTo(this);
                });


                //IF THERE ARE MORE THAN 3 EVENTS ON THE SAME DAY, ADD ANCHOR
                $(".ui-widget.app.upcomingevents").each(function() {
                    var viewCalendarLink = $(".view-calendar-link", this).attr("href");


                    $(".ui-articles > li", this).each(function() {
                    var eventCount = $(this).find('.ui-article-description').length;

                    if($(eventCount > 3)) {
                    	$('.ui-article-description:nth-child(n+4)',this).remove();
                    }


                        if($(".upcoming-column.right .ui-article-description", this).length > 1) {
                            var eventUrl = ((($(".upcoming-column.right .ui-article-description:first-child a", this).attr("href")).split("#")[1]).split("/"));
                            var calendarUrl =  viewCalendarLink + "#" + eventUrl[0] + "/" + eventUrl[1] + "/day";
                            $(".upcoming-column.right", this).append('
More');
                        }
                    });
                });


        },

        "MoveElements":function() {
        	var _this = this;


        	$(window).scroll(function() {
                var headerHeight = $('header').outerHeight();

               if(_this.GetBreakPoint() == 'desktop') {

                    //MOVE VIEW CALENDAR LINK TO UPCOMING EVENTS HEADER
                	$('#bottom-apps .view-calendar-link').appendTo('.hp .upcomingevents .ui-widget-header');

                    //MOVE MORE LINK TO HF HEADER
            		$('#bottom-apps .headlines .more-link').appendTo('#bottom-apps .headlines .ui-widget-header');

					//FIXED HEADER ANIMATION
                    if ($(this).scrollTop() > headerHeight + 50) {  
                      $('header').addClass("sticky");
                      $('body').css('padding-top', '220px');
                      $('#cs-contrast-bar-outer, .sw-skipnav-outerbar').hide();
                    }
                    else {
                      $('header').removeClass("sticky");
                      $('body').css('padding-top','0');
                      $('#cs-contrast-bar-outer, .sw-skipnav-outerbar').show();
                    }

                    if ($(this).scrollTop() > headerHeight + 100){  
                      $('header').addClass("animate");
                    }
                    else {
                      $('header').removeClass("animate");

                    }



                } else {
                	//REMOVE STICKY HEADER
                	$('header').removeClass("sticky");
                    $('header').removeClass("animate");
                    $('body').css('padding-top','0');
                    $('#cs-contrast-bar-outer, .sw-skipnav-outerbar').show();


                    //MOVE VIEW CALENDAR LINK TO UPCOMING EVENTS TO WIDGET DETAIL
                	$('.hp .view-calendar-link').appendTo('.hp .upcomingevents .ui-widget-detail');

                    //MOVE MORE LINK TO HF DETAIL
            		$('#bottom-apps .headlines .more-link').appendTo('#bottom-apps .headlines .ui-widget-detail');
                }




            });

            //MOVE FOOTER ELEMENTS
            if(_this.GetBreakPoint() !== 'desktop') {
            	$('#disclaimer').insertBefore('#sitemap');
                $('#hp-slideshow .mmg-description-outer').appendTo('#cs-fullscreen-video-outer');



            } else {
            	$('#disclaimer').insertAfter('#sitemap');
            }

            var appHeight = $('#hp-app-wrap').outerHeight();
            $('#hp-multi-column .color').css('height', appHeight + 69);

            $('#hp-app-wrap .app').on('click', function() {

                setTimeout(function() {
                	var appHeight = $('#hp-app-wrap').outerHeight();
            		$('#hp-multi-column .color').css('height', appHeight + 173);
                },300);
            })






        },

        "GetBreakPoint":function() {
        	return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

